#include <websocketpp/config/debug_asio_no_tls.hpp>
#include <websocketpp/server.hpp>
#include <websocketpp/logger/syslog.hpp>
#include <iostream>

using websocketpp::lib::placeholders::_1;
using websocketpp::lib::placeholders::_2;
using websocketpp::lib::bind;

class WebSocketServer {
public:
    typedef websocketpp::server<websocketpp::config::debug_asio> server;
    typedef server::message_ptr message_ptr;

    WebSocketServer() {
        // Set logging settings
        m_server.set_access_channels(websocketpp::log::alevel::all);
        m_server.clear_access_channels(websocketpp::log::alevel::frame_payload);

        // Initialize ASIO
        m_server.init_asio();
        m_server.set_reuse_addr(true);

        // Register handlers
        m_server.set_message_handler(bind(&WebSocketServer::on_message, this, ::_1, ::_2));
        m_server.set_http_handler(bind(&WebSocketServer::on_http, this, ::_1));
        m_server.set_fail_handler(bind(&WebSocketServer::on_fail, this, ::_1));
        m_server.set_close_handler(bind(&WebSocketServer::on_close, this, ::_1));
        m_server.set_validate_handler(bind(&WebSocketServer::validate, this, ::_1));
    }

    void run(int port) {
        try {
            // Listen on specified port
            m_server.listen(port);

            // Start the server accept loop
            m_server.start_accept();

            // Start the ASIO io_service run loop
            m_server.run();
        } catch (websocketpp::exception const & e) {
            std::cout << e.what() << std::endl;
        }
    }

private:
    bool validate(websocketpp::connection_hdl hdl) {
        return true;
    }

    void on_http(websocketpp::connection_hdl hdl) {
        server::connection_ptr con = m_server.get_con_from_hdl(hdl);
        std::string res = con->get_request_body();
        std::stringstream ss;
        ss << "got HTTP request with " << res.size() << " bytes of body data.";
        con->set_body(ss.str());
        con->set_status(websocketpp::http::status_code::ok);
    }

    void on_fail(websocketpp::connection_hdl hdl) {
        server::connection_ptr con = m_server.get_con_from_hdl(hdl);
        std::cout << "Fail handler: " << con->get_ec() << " " << con->get_ec().message() << std::endl;
    }

    void on_close(websocketpp::connection_hdl hdl) {
        std::cout << "Close handler" << std::endl;
    }

    void on_message(websocketpp::connection_hdl hdl, message_ptr msg) {
        std::cout << "on_message called with hdl: " << hdl.lock().get()
                  << " and message: " << msg->get_payload()
                  << std::endl;

        try {
            m_server.send(hdl, msg->get_payload(), msg->get_opcode());
        } catch (websocketpp::exception const & e) {
            std::cout << "Echo failed because: " 
                      << "(" << e.what() << ")" << std::endl;
        }
    }

    server m_server;
};
