#include <curl/curl.h>
#include <iostream>
#include <fstream>
#include <string>
#include <thread>
#include <mutex>
#include <queue>
#include <unordered_set>

class Task
{
public:
    Task(const std::string& url, const std::string& output) : m_url(url), m_output(output) {}

    void run()
    {
        std::ofstream output(m_output, std::fstream::in | std::fstream::out | std::fstream::trunc);
        output.close();

        CURL *curl_handler = curl_easy_init();
        curl_easy_setopt(curl_handler, CURLOPT_URL, m_url.c_str());
        curl_easy_setopt(curl_handler, CURLOPT_TIMEOUT, 60);
        curl_easy_setopt(curl_handler, CURLOPT_WRITEFUNCTION, &Task::writeData);
        curl_easy_setopt(curl_handler, CURLOPT_WRITEDATA, this);

        curl_easy_perform(curl_handler);

        curl_easy_cleanup(curl_handler);
    }

    size_t writeData(void *buffer, size_t size, size_t nmemb)
    {
        std::ofstream file;
        file.open(m_output, std::fstream::in | std::fstream::out | std::fstream::app | std::fstream::binary);
        size_t bytesToWrite = size * nmemb;
        file.write(reinterpret_cast<const char *>(buffer), bytesToWrite);
        file.close();

        return bytesToWrite;
    }

private:
    std::string m_url;
    std::string m_output;
};

class DownloadPool
{
public:
    DownloadPool()
    {
        m_noMoreTask = false;
        curl_global_init(CURL_GLOBAL_ALL);

        for (int idx = 0; idx < 4; idx++)
        {
            m_threads.push_back(std::thread(&DownloadPool::run, this));
        }
    }

    ~DownloadPool()
    {
        curl_global_cleanup();
    }

    void addTask(const std::string &url, const std::string& output)
    {
        std::string uid = url + output;
        bool existed = false;
        Task task(url, output);
        m_mtx.lock();
        if (!(existed = (m_urls.find(uid) != m_urls.cend())))
        {
            m_urls.insert(uid);
            m_queue.push(task);
        }
        m_mtx.unlock();
    }

    void setNoMoreTask()
    {
        m_mtx.lock();
        m_noMoreTask = true;
        m_mtx.unlock();
    }

    void finishAndWaitForExit()
    {
        setNoMoreTask();
        for (auto& thread : m_threads)
        {
            thread.join();
        }
    }

private:
    void run()
    {
        while(true)
        {
            bool found = false;
            Task task;

            m_mtx.lock();
            if (!m_queue.empty())
            {
                task = m_queue.front();
                m_queue.pop();
                found = true;
            }
            bool noMoreTask = m_noMoreTask;
            m_mtx.unlock();

            if (found)
            {
                task.run();
                continue;
            }
            if (noMoreTask)
            {
                break;
            }
            std::this_thread::sleep_for(std::chrono::milliseconds(100));
        }
    }

    std::vector<std::thread> m_threads;
    std::queue<Task> m_queue;
    std::unordered_set<std::string> m_urls;
    std::mutex m_mtx;
    bool m_noMoreTask;
};
