import (
	"errors"
	"log"
	"net"
	"strconv"
	"strings"

	"github.com/gogo/protobuf/proto"
	cache "github.com/patrickmn/go-cache"
	"github.com/txthinking/crypto"
)

type Server struct {
	TCPAddr   *net.TCPAddr
	UDPAddr   *net.UDPAddr
	TCPListen *net.TCPListener
	UDPConn   *net.UDPConn
	Cache     *cache.Cache
	Ckv       *crypto.KV
	PortCkv   map[int64]*crypto.KV
}

func NewServer(addr, password string, portPassword []string) (*Server, error) {
	pc := make(map[int64]*crypto.KV)
	for _, v := range portPassword {
		l := strings.Split(v, " ")
		if len(l) != 2 {
			return nil, errors.New("Wrong format: " + v)
		}
		port, err := strconv.ParseInt(l[0], 10, 64)
		if err != nil {
			return nil, err
		}
		ckv := &crypto.KV{
			AESKey: []byte(l[1]),
		}
		pc[port] = ckv
	}
	taddr, err := net.ResolveTCPAddr("tcp", addr)
	if err != nil {
		return nil, err
	}
	uaddr, err := net.ResolveUDPAddr("udp", addr)
	if err != nil {
		return nil, err
	}
	s := &Server{
		TCPAddr: taddr,
		UDPAddr: uaddr,
		Cache:   cache.New(cache.NoExpiration, cache.NoExpiration),
		Ckv: &crypto.KV{
			AESKey: []byte(password),
		},
		PortCkv: pc,
	}
	return s, nil
}

func (s *Server) RunUDPServer() error {
	var err error
	s.UDPConn, err = net.ListenUDP("udp", s.UDPAddr)
	if err != nil {
		return err
	}
	defer s.UDPConn.Close()
	for {
		b := make([]byte, 65536)
		n, addr, err := s.UDPConn.ReadFromUDP(b)
		if err != nil {
			return err
		}
		go func(addr *net.UDPAddr, b []byte) {
			if err := s.UDPHandle(addr, b); err != nil {
				log.Println(err)
			}
		}(addr, b[0:n])
	}
	return nil
}

func (s *Server) UDPHandle(addr *net.UDPAddr, b []byte) error {
	p := &UDPPacket{}
	if err := proto.Unmarshal(b, p); err != nil {
		return nil
	}
	if p.Address == "" {
		u, err := NewUDPServer(s, p, addr)
		if err != nil {
			return err
		}
		s.Cache.Set("u:"+strconv.FormatInt(p.Port, 10), u, cache.DefaultExpiration)
		defer s.Cache.Delete("u:" + strconv.FormatInt(p.Port, 10))
		if err := u.ListenAndServe(); err != nil {
			return err
		}
		return nil
	}
	i, ok := s.Cache.Get("u:" + strconv.FormatInt(p.Port, 10))
	if !ok {
		return nil
	}
	c := i.(*UDPServer)
	if err := c.HandlePacket(p); err != nil {
		return err
	}
	return nil
}
