import java.io.IOException;
import java.lang.foreign.Arena;
import java.nio.channels.FileChannel;
import java.nio.file.Path;
import java.nio.file.StandardOpenOption;

public class FileChannelInteractor {

    private FileChannel fileChannel;
    private long mapAddress;
    private long fileSize;

    public FileChannelInteractor(String filePath) throws IOException {
        this.fileChannel = FileChannel.open(Path.of(filePath), StandardOpenOption.READ);
        this.fileSize = fileChannel.size();
        this.mapAddress = fileChannel.map(FileChannel.MapMode.READ_ONLY, 0, fileSize, Arena.global()).address();
    }

    public long getMemoryMappedAddress() {
        return mapAddress;
    }

    public long getFileSize() {
        return fileSize;
    }

    public void close() throws IOException {
        fileChannel.close();
    }

    public static void spawnWorkerProcess() throws IOException {
        ProcessHandle.Info info = ProcessHandle.current().info();
        ArrayList<String> workerCommand = new ArrayList<>();
        info.command().ifPresent(workerCommand::add);
        info.arguments().ifPresent(args -> workerCommand.addAll(Arrays.asList(args)));
        workerCommand.add("--worker");
        new ProcessBuilder()
                .command(workerCommand)
                .inheritIO()
                .redirectOutput(ProcessBuilder.Redirect.PIPE)
                .start()
                .getInputStream()
                .transferTo(System.out);
    }
}
