import java.io.IOException;
import java.util.Map;
import java.util.concurrent.TimeoutException;
import com.rabbitmq.client.ConnectionFactory;
import com.rabbitmq.client.Connection;
import com.rabbitmq.client.Channel;
import com.rabbitmq.client.MessageProperties;

public class RabbitMQProducer {

    private String host;
    private int port;
    private String username;
    private String password;
    private String queueName;
    private boolean durable;
    private Connection connection;
    private Channel channel;

    public RabbitMQProducer(Map<String, String> config) {
        this.host = config.get("rabbitmq_host");
        this.port = Integer.parseInt(config.get("rabbitmq_port"));
        this.username = config.get("rabbitmq_user");
        this.password = config.get("rabbitmq_pass");
        this.queueName = config.get("rabbitmq_queuename");
        this.durable = config.get("rabbitmq_durable").equals("true");
    }

    public void connect() throws IOException, TimeoutException {
        ConnectionFactory factory = new ConnectionFactory();
        factory.setHost(host);
        factory.setPort(port);
        factory.setUsername(username);
        factory.setPassword(password);
        connection = factory.newConnection();
        channel = connection.createChannel();
        channel.queueDeclare(queueName, durable, false, false, null);
    }

    public void sendMessage(String message) throws IOException {
        channel.basicPublish("", queueName, 
            MessageProperties.PERSISTENT_TEXT_PLAIN,
            message.getBytes()
        );
    }

    public void sendMessages(String[] messages) throws IOException {
        for (String message : messages) {
            sendMessage(message);
        }
    }

    public void close() throws IOException, TimeoutException {
        if (channel != null && channel.isOpen()) {
            channel.close();
        }
        if (connection != null && connection.isOpen()) {
            connection.close();
        }
    }
}
