import redis.clients.jedis.JedisPooled;
import redis.clients.jedis.search.*;
import redis.clients.jedis.search.aggr.*;
import redis.clients.jedis.search.schemafields.*;

public class RedisSearchClient {

    private JedisPooled jedis;

    public RedisSearchClient(String host, int port) {
        this.jedis = new JedisPooled(host, port);
    }

    public void createIndex(String indexName, String prefix, SchemaField[] schema) {
        jedis.ftCreate(indexName,
            FTCreateParams.createParams()
                .on(IndexDataType.JSON)
                .addPrefix(prefix),
            schema
        );
    }

    public void addDocument(String key, Object document) {
        jedis.jsonSetWithEscape(key, document);
    }

    public List<Document> search(String indexName, Query query) {
        return jedis.ftSearch(indexName, query).getDocuments();
    }

    public AggregationResult aggregate(String indexName, AggregationBuilder aggregationBuilder) {
        return jedis.ftAggregate(indexName, aggregationBuilder);
    }

    public void close() {
        jedis.close();
    }
}
