import io.lettuce.authx.TokenBasedRedisCredentialsProvider;
import io.lettuce.core.ClientOptions;
import io.lettuce.core.RedisClient;
import io.lettuce.core.RedisURI;
import io.lettuce.core.SocketOptions;
import io.lettuce.core.TimeoutOptions;
import io.lettuce.core.api.StatefulRedisConnection;
import io.lettuce.core.cluster.ClusterClientOptions;
import io.lettuce.core.cluster.RedisClusterClient;
import io.lettuce.core.cluster.api.StatefulRedisClusterConnection;
import io.lettuce.core.cluster.api.sync.NodeSelection;
import io.lettuce.core.cluster.api.sync.RedisAdvancedClusterCommands;
import io.lettuce.core.codec.StringCodec;
import redis.clients.authentication.core.IdentityProviderConfig;
import redis.clients.authentication.core.TokenAuthConfig;
import redis.clients.authentication.entraid.EntraIDTokenAuthConfigBuilder;

import java.time.Duration;
import java.util.Collections;
import java.util.Set;

public class RedisConnectionManager {

    private RedisClient redisClient;
    private RedisClusterClient redisClusterClient;
    private TokenBasedRedisCredentialsProvider credentialsUser1;
    private TokenBasedRedisCredentialsProvider credentialsUser2;

    public RedisConnectionManager(String redisUri, String authority, Set<String> scopes, 
                                String user1ClientId, String user1Secret, 
                                String user2ClientId, String user2Secret) {
        IdentityProviderConfig config1;
        try (EntraIDTokenAuthConfigBuilder builder = EntraIDTokenAuthConfigBuilder.builder()) {
            config1 = builder.authority(authority).clientId(user1ClientId).secret(user1Secret).scopes(scopes)
                    .tokenRequestExecTimeoutInMs(10000).build().getIdentityProviderConfig();
        }

        TokenAuthConfig tokenAuthConfigUser1 = TokenAuthConfig.builder().tokenRequestExecTimeoutInMs(10000)
                .expirationRefreshRatio(0.1f).identityProviderConfig(config1).build();
        credentialsUser1 = TokenBasedRedisCredentialsProvider.create(tokenAuthConfigUser1);

        IdentityProviderConfig config2 = EntraIDTokenAuthConfigBuilder.builder().authority(authority).clientId(user2ClientId)
                .secret(user2Secret).scopes(scopes).tokenRequestExecTimeoutInMs(10000).build().getIdentityProviderConfig();
        TokenAuthConfig tokenAuthConfigUser2 = TokenAuthConfig.builder().tokenRequestExecTimeoutInMs(10000)
                .expirationRefreshRatio(0.1f).identityProviderConfig(config2).build();
        credentialsUser2 = TokenBasedRedisCredentialsProvider.create(tokenAuthConfigUser2);

        RedisURI redisURI1 = RedisURI.create(redisUri);
        redisURI1.setCredentialsProvider(credentialsUser1);

        RedisURI redisURI2 = RedisURI.create(redisUri);
        redisURI2.setCredentialsProvider(credentialsUser2);

        ClientOptions clientOptions = ClientOptions.builder()
                .socketOptions(SocketOptions.builder().connectTimeout(Duration.ofSeconds(5)).build())
                .disconnectedBehavior(ClientOptions.DisconnectedBehavior.REJECT_COMMANDS)
                .timeoutOptions(TimeoutOptions.enabled(Duration.ofSeconds(1)))
                .reauthenticateBehavior(ClientOptions.ReauthenticateBehavior.ON_NEW_CREDENTIALS).build();

        redisClient = RedisClient.create(redisURI1);
        redisClient.setOptions(clientOptions);
    }

    public StatefulRedisConnection<String, String> createStandaloneConnection() {
        return redisClient.connect(StringCodec.UTF8);
    }

    public StatefulRedisConnection<String, String> createStandaloneConnectionWithUser2() {
        RedisURI redisURI2 = RedisURI.create(REDIS_URI);
        redisURI2.setCredentialsProvider(credentialsUser2);
        return redisClient.connect(StringCodec.UTF8, redisURI2);
    }

    public StatefulRedisClusterConnection<String, String> createClusterConnection() {
        ClusterClientOptions clusterClientOptions = ClusterClientOptions.builder()
                .socketOptions(SocketOptions.builder().connectTimeout(Duration.ofSeconds(5)).build())
                .disconnectedBehavior(ClientOptions.DisconnectedBehavior.REJECT_COMMANDS)
                .timeoutOptions(TimeoutOptions.enabled(Duration.ofSeconds(1)))
                .reauthenticateBehavior(ClientOptions.ReauthenticateBehavior.ON_NEW_CREDENTIALS).build();

        redisClusterClient = RedisClusterClient.create(redisURI1);
        redisClusterClient.setOptions(clusterClientOptions);
        return redisClusterClient.connect(StringCodec.UTF8);
    }

    public void close() {
        if (redisClient != null) {
            redisClient.shutdown();
        }
        if (redisClusterClient != null) {
            redisClusterClient.shutdown();
        }
        if (credentialsUser1 != null) {
            credentialsUser1.close();
        }
        if (credentialsUser2 != null) {
            credentialsUser2.close();
        }
    }
}
