import redis.clients.authentication.core.IdentityProvider;
import redis.clients.authentication.core.IdentityProviderConfig;
import redis.clients.authentication.core.SimpleToken;
import redis.clients.authentication.core.Token;
import redis.clients.authentication.entraid.EntraIDTokenAuthConfigBuilder;
import redis.clients.jedis.Connection;
import redis.clients.jedis.ConnectionPoolConfig;
import redis.clients.jedis.DefaultJedisClientConfig;
import redis.clients.jedis.EndpointConfig;
import redis.clients.jedis.HostAndPort;
import redis.clients.jedis.JedisClientConfig;
import redis.clients.jedis.JedisCluster;

public class RedisClusterAuthManager {

    private final IdentityProviderConfig idpConfig;
    private final AuthXManager authXManager;
    private final JedisClientConfig config;
    private final ConnectionPoolConfig poolConfig;
    private final HostAndPort hostAndPort;

    public RedisClusterAuthManager(HostAndPort hostAndPort) {
        this.hostAndPort = hostAndPort;
        this.idpConfig = new IdentityProviderConfig() {
            @Override
            public IdentityProvider getProvider() {
                return new IdentityProvider() {
                    @Override
                    public Token requestToken() {
                        return new SimpleToken("default", "cluster",
                                System.currentTimeMillis() + 5 * 1000, System.currentTimeMillis(),
                                null);
                    }
                };
            }
        };
        this.authXManager = new AuthXManager(EntraIDTokenAuthConfigBuilder.builder()
                .lowerRefreshBoundMillis(1000).identityProviderConfig(idpConfig).build());
        this.config = DefaultJedisClientConfig.builder().authXManager(authXManager).build();
        this.poolConfig = new ConnectionPoolConfig();
    }

    public void executeRedisOperations() {
        try (JedisCluster jc = new JedisCluster(hostAndPort, config, 5, poolConfig)) {
            jc.set("foo", "bar");
            jc.get("foo");
            jc.del("foo");
        }
    }

    public void testReAuthentication() throws InterruptedException, ExecutionException {
        AuthXManager spyManager = spy(authXManager);
        List<Connection> connections = new ArrayList<>();

        doAnswer(invocation -> {
            Connection connection = spy((Connection) invocation.getArgument(0));
            invocation.getArguments()[0] = connection;
            connections.add(connection);
            Object result = invocation.callRealMethod();
            return result;
        }).when(spyManager).addConnection(any(Connection.class));

        JedisClientConfig spyConfig = DefaultJedisClientConfig.builder().authXManager(spyManager).build();

        ExecutorService executorService = Executors.newFixedThreadPool(2);
        CountDownLatch latch = new CountDownLatch(1);

        try (JedisCluster jc = new JedisCluster(Collections.singleton(hostAndPort), spyConfig)) {
            Runnable task = () -> {
                while (latch.getCount() > 0) {
                    jc.set("foo", "bar");
                }
            };
            Future task1 = executorService.submit(task);
            Future task2 = executorService.submit(task);

            await().pollInterval(ONE_HUNDRED_MILLISECONDS).atMost(ONE_SECOND)
                    .until(connections::size, greaterThanOrEqualTo(2));

            connections.forEach(conn -> {
                verify(conn, atLeast(2)).reAuthenticate();
            });

            latch.countDown();
            task1.get();
            task2.get();
        } finally {
            latch.countDown();
            executorService.shutdown();
        }
    }
}
