import redis.clients.jedis.Jedis;
import redis.clients.jedis.JedisPool;
import redis.clients.jedis.JedisPoolConfig;
import redis.clients.jedis.exceptions.JedisException;

public class RedisConnectionManager {

    private JedisPool jedisPool;

    public RedisConnectionManager(String host, int port, String username, String password) {
        JedisPoolConfig poolConfig = new JedisPoolConfig();
        this.jedisPool = new JedisPool(poolConfig, host, port, username, password);
    }

    public void executeCommand(RedisCommand command) {
        try (Jedis jedis = jedisPool.getResource()) {
            switch (command.getOperation()) {
                case "SET":
                    jedis.set(command.getKey(), command.getValue());
                    break;
                case "GET":
                    String result = jedis.get(command.getKey());
                    command.setResult(result);
                    break;
                case "INCR":
                    jedis.incr(command.getKey());
                    break;
                default:
                    throw new JedisException("Unsupported operation: " + command.getOperation());
            }
        }
    }

    public void close() {
        if (jedisPool != null && !jedisPool.isClosed()) {
            jedisPool.close();
        }
    }

    public boolean isClosed() {
        return jedisPool == null || jedisPool.isClosed();
    }
}
