import redis.clients.jedis.*;
import org.apache.commons.pool2.impl.GenericObjectPoolConfig;
import java.util.concurrent.atomic.AtomicInteger;

public class RedisConnectionPool {

    private JedisPool pool;
    private EndpointConfig endpoint;

    public RedisConnectionPool(EndpointConfig endpoint) {
        this.endpoint = endpoint;
        this.pool = new JedisPool(new GenericObjectPoolConfig<Jedis>(), 
            endpoint.getHost(), endpoint.getPort(), 2000, endpoint.getPassword());
    }

    public void executeOperations(int totalOperations) {
        AtomicInteger ind = new AtomicInteger();
        for (int i = 0; i < 50; i++) {
            new Thread(() -> {
                for (int j = 0; (j = ind.getAndIncrement()) < totalOperations;) {
                    try (Jedis jedis = pool.getResource()) {
                        String key = "foo" + j;
                        jedis.set(key, key);
                        jedis.get(key);
                    } catch (Exception e) {
                        e.printStackTrace();
                    }
                }
            }).start();
        }
    }

    public void close() {
        if (pool != null) {
            pool.destroy();
        }
    }
}
