import com.github.ltsopensource.core.logger.Logger;
import com.github.ltsopensource.core.logger.LoggerFactory;
import redis.clients.jedis.Jedis;
import redis.clients.jedis.JedisPool;
import redis.clients.jedis.JedisPoolConfig;

public class RedisPubSubManager {

    private static final Logger logger = LoggerFactory.getLogger(RedisPubSubManager.class);
    private final JedisPool jedisPool;
    private final String channelName;

    public RedisPubSubManager(String host, int port, String channelName) {
        JedisPoolConfig poolConfig = new JedisPoolConfig();
        this.jedisPool = new JedisPool(poolConfig, host, port, 0);
        this.channelName = channelName;
    }

    public void startSubscriber(Subscriber subscriber) {
        Jedis subscriberJedis = jedisPool.getResource();
        new Thread(() -> {
            try {
                logger.info("Subscribing to \"" + channelName + "\". This thread will be blocked.");
                subscriberJedis.subscribe(subscriber, channelName);
                logger.info("Subscription ended.");
            } catch (Exception e) {
                logger.error("Subscribing failed.", e);
            } finally {
                subscriberJedis.close();
            }
        }).start();
    }

    public void startPublisher() {
        Jedis publisherJedis = jedisPool.getResource();
        new Publisher(publisherJedis, channelName).start();
        publisherJedis.close();
    }

    public void shutdown() {
        jedisPool.destroy();
    }
}
