import io.lettuce.core.*;
import io.lettuce.core.api.sync.RedisCommands;

public class RedisGeoOperations {

    private RedisClient redisClient;
    private RedisCommands<String, String> redisCommands;

    public RedisGeoOperations(String host, int port) {
        this.redisClient = RedisClient.create(RedisURI.Builder.redis(host, port).build());
        this.redisCommands = redisClient.connect().sync();
    }

    public void addGeoLocation(String key, double longitude, double latitude, String member) {
        redisCommands.geoadd(key, longitude, latitude, member);
    }

    public Set<String> getGeoRadius(String key, double longitude, double latitude, double radius, GeoArgs.Unit unit) {
        return redisCommands.georadius(key, longitude, latitude, radius, unit);
    }

    public Double getGeoDistance(String key, String member1, String member2, GeoArgs.Unit unit) {
        return redisCommands.geodist(key, member1, member2, unit);
    }

    public List<GeoWithin<String>> getGeoRadiusWithArgs(String key, double longitude, double latitude, double radius, 
                                                      GeoArgs.Unit unit, GeoArgs geoArgs) {
        return redisCommands.georadius(key, longitude, latitude, radius, unit, geoArgs);
    }

    public List<GeoCoordinates> getGeoPositions(String key, String... members) {
        return redisCommands.geopos(key, members);
    }

    public void closeConnection() {
        redisCommands.getStatefulConnection().close();
        redisClient.shutdown();
    }
}
