import io.lettuce.core.ReadFrom;
import io.lettuce.core.RedisClient;
import io.lettuce.core.RedisURI;
import io.lettuce.core.codec.StringCodec;
import io.lettuce.core.masterreplica.MasterReplica;
import io.lettuce.core.masterreplica.StatefulRedisMasterReplicaConnection;

public class RedisMasterSlaveConnector {

    private RedisClient redisClient;
    private StatefulRedisMasterReplicaConnection<String, String> connection;

    public void connectToMasterSlave(String sentinelUrl) {
        redisClient = RedisClient.create();
        connection = MasterReplica.connect(redisClient, StringCodec.UTF8, RedisURI.create(sentinelUrl));
        connection.setReadFrom(ReadFrom.UPSTREAM_PREFERRED);
    }

    public StatefulRedisMasterReplicaConnection<String, String> getConnection() {
        return connection;
    }

    public void closeConnection() {
        if (connection != null) {
            connection.close();
        }
        if (redisClient != null) {
            redisClient.shutdown();
        }
    }
}
