import io.lettuce.core.RedisClient;
import io.lettuce.core.api.async.RedisAsyncCommands;
import io.lettuce.core.api.StatefulRedisConnection;
import java.util.HashMap;
import java.util.Map;
import java.util.concurrent.CompletableFuture;

public class RedisHashInteraction {

    private RedisClient redisClient;
    private StatefulRedisConnection<String, String> connection;
    private RedisAsyncCommands<String, String> asyncCommands;

    public RedisHashInteraction(String redisUri) {
        this.redisClient = RedisClient.create(redisUri);
        this.connection = redisClient.connect();
        this.asyncCommands = connection.async();
    }

    public CompletableFuture<Long> deleteKeys(String... keys) {
        return asyncCommands.del(keys).toCompletableFuture();
    }

    public CompletableFuture<Long> setHash(String key, Map<String, String> hash) {
        return asyncCommands.hset(key, hash).toCompletableFuture();
    }

    public CompletableFuture<String> getHashField(String key, String field) {
        return asyncCommands.hget(key, field).toCompletableFuture();
    }

    public CompletableFuture<Map<String, String>> getAllHashFields(String key) {
        return asyncCommands.hgetall(key).toCompletableFuture();
    }

    public CompletableFuture<Long> incrementHashField(String key, String field, long amount) {
        return asyncCommands.hincrby(key, field, amount).toCompletableFuture();
    }

    public void closeConnection() {
        if (connection != null) {
            connection.close();
        }
        if (redisClient != null) {
            redisClient.shutdown();
        }
    }
}
