import io.lettuce.core.RedisClient;
import io.lettuce.core.RedisURI;
import io.lettuce.core.api.StatefulRedisConnection;
import io.lettuce.core.codec.ByteArrayCodec;
import io.lettuce.test.settings.TestSettings;
import java.util.concurrent.CompletableFuture;

public class RedisConnectionManager {

    private RedisClient redisClient;
    private StatefulRedisConnection<byte[], byte[]> connection;
    private static final int BATCH_SIZE = 20;
    private CompletableFuture[] commands;

    public void setupConnection() {
        redisClient = RedisClient.create(RedisURI.create(TestSettings.host(), TestSettings.port()));
        connection = redisClient.connect(ByteArrayCodec.INSTANCE);
        commands = new CompletableFuture[BATCH_SIZE];
    }

    public StatefulRedisConnection<byte[], byte[]> getConnection() {
        return connection;
    }

    public void closeConnection() {
        if (connection != null) {
            connection.close();
        }
        if (redisClient != null) {
            redisClient.shutdown();
        }
    }

    public CompletableFuture[] getCommandFutures() {
        return commands;
    }

    public int getBatchSize() {
        return BATCH_SIZE;
    }
}
