import org.apache.rocketmq.client.consumer.DefaultMQPushConsumer;
import org.apache.rocketmq.client.consumer.listener.ConsumeConcurrentlyContext;
import org.apache.rocketmq.client.consumer.listener.ConsumeConcurrentlyStatus;
import org.apache.rocketmq.client.consumer.listener.MessageListenerConcurrently;
import org.apache.rocketmq.client.exception.MQClientException;
import org.apache.rocketmq.common.message.MessageExt;
import java.util.List;

public class SimpleConsumer {
    private DefaultMQPushConsumer consumer;

    public SimpleConsumer(String consumerGroup, String namesrvAddr) {
        consumer = new DefaultMQPushConsumer(consumerGroup);
        consumer.setNamesrvAddr(namesrvAddr);
    }

    public void subscribe(String topic, String subExpression) throws MQClientException {
        consumer.subscribe(topic, subExpression);
    }

    public void registerMessageListener(MessageListenerConcurrently listener) {
        consumer.registerMessageListener(listener);
    }

    public void start() throws MQClientException {
        consumer.start();
    }

    public void shutdown() {
        consumer.shutdown();
    }

    public static MessageListenerConcurrently createDefaultListener() {
        return new MessageListenerConcurrently() {
            @Override
            public ConsumeConcurrentlyStatus consumeMessage(List<MessageExt> msgs, ConsumeConcurrentlyContext context) {
                for (MessageExt msg : msgs) {
                    System.out.println(msg.getKeys() + ":" + new String(msg.getBody()));
                }
                return ConsumeConcurrentlyStatus.CONSUME_SUCCESS;
            }
        };
    }
}
