import org.apache.rocketmq.client.consumer.DefaultMQPushConsumer;
import org.apache.rocketmq.client.consumer.listener.ConsumeConcurrentlyContext;
import org.apache.rocketmq.client.consumer.listener.ConsumeConcurrentlyStatus;
import org.apache.rocketmq.client.consumer.listener.MessageListenerConcurrently;
import org.apache.rocketmq.client.exception.MQClientException;
import org.apache.rocketmq.common.consumer.ConsumeFromWhere;
import org.apache.rocketmq.common.protocol.heartbeat.MessageModel;

import java.util.List;

public class RocketMQConsumer {

    private DefaultMQPushConsumer pushConsumer;

    public void initializeConsumer(String consumerGroup, String namesrvAddr) throws MQClientException {
        pushConsumer = new DefaultMQPushConsumer(consumerGroup);
        pushConsumer.setNamesrvAddr(namesrvAddr);
        pushConsumer.setConsumeFromWhere(ConsumeFromWhere.CONSUME_FROM_LAST_OFFSET);
        pushConsumer.setMessageModel(MessageModel.CLUSTERING);
    }

    public void subscribeToTopic(String topic, String tagExpression) throws MQClientException {
        pushConsumer.subscribe(topic, tagExpression);
    }

    public void registerMessageListener(MessageListenerConcurrently listener) {
        pushConsumer.registerMessageListener(new MessageListenerConcurrently() {
            @Override
            public ConsumeConcurrentlyStatus consumeMessage(List<MessageExt> msgs,
                ConsumeConcurrentlyContext context) {
                return listener.consumeMessage(msgs, context);
            }
        });
    }

    public void startConsumer() throws MQClientException {
        pushConsumer.start();
    }

    public void shutdownConsumer() {
        pushConsumer.shutdown();
    }
}
