import org.apache.rocketmq.client.consumer.DefaultMQPushConsumer;
import org.apache.rocketmq.client.consumer.listener.ConsumeOrderlyContext;
import org.apache.rocketmq.client.consumer.listener.ConsumeOrderlyStatus;
import org.apache.rocketmq.client.consumer.listener.MessageListenerOrderly;
import org.apache.rocketmq.client.exception.MQClientException;
import org.apache.rocketmq.common.consumer.ConsumeFromWhere;
import org.apache.rocketmq.common.message.MessageExt;

import java.util.List;

public class RocketMQOrderedConsumer {

    private DefaultMQPushConsumer consumer;

    public RocketMQOrderedConsumer(String namesrvAddr, String consumerGroup) throws MQClientException {
        consumer = new DefaultMQPushConsumer(consumerGroup);
        consumer.setNamesrvAddr(namesrvAddr);
        consumer.setConsumeFromWhere(ConsumeFromWhere.CONSUME_FROM_FIRST_OFFSET);
    }

    public void subscribe(String topic, String subExpression) throws MQClientException {
        consumer.subscribe(topic, subExpression);
    }

    public void registerMessageListener(MessageListenerOrderly listener) {
        consumer.registerMessageListener(listener);
    }

    public void start() throws MQClientException {
        consumer.start();
    }

    public void shutdown() {
        consumer.shutdown();
    }

    public static class OrderedMessageListener implements MessageListenerOrderly {
        @Override
        public ConsumeOrderlyStatus consumeMessage(List<MessageExt> msgs, ConsumeOrderlyContext context) {
            System.out.printf(System.currentTimeMillis() + "," + Thread.currentThread().getName() + " Receive New Messages: " + msgs + "%n");
            return ConsumeOrderlyStatus.SUCCESS;
        }
    }
}
