import org.apache.rocketmq.client.exception.MQClientException;
import org.apache.rocketmq.client.producer.DefaultMQProducer;
import org.apache.rocketmq.client.producer.SendResult;
import org.apache.rocketmq.common.message.Message;
import org.apache.rocketmq.remoting.common.RemotingHelper;

public class Producer {

    private DefaultMQProducer producer;

    public Producer(String producerGroup, String namesrvAddr) throws MQClientException {
        this.producer = new DefaultMQProducer(producerGroup);
        this.producer.setNamesrvAddr(namesrvAddr);
    }

    public void start() throws MQClientException {
        producer.start();
    }

    public SendResult sendMessage(String topic, String tag, String messageBody) throws Exception {
        Message msg = new Message(topic, 
                                tag,
                                messageBody.getBytes(RemotingHelper.DEFAULT_CHARSET));
        return producer.send(msg);
    }

    public void shutdown() {
        producer.shutdown();
    }
}
