import org.apache.rocketmq.client.QueryResult;
import org.apache.rocketmq.client.exception.MQClientException;
import org.apache.rocketmq.client.producer.DefaultMQProducer;
import org.apache.rocketmq.client.producer.SendResult;
import org.apache.rocketmq.common.message.Message;
import org.apache.rocketmq.common.message.MessageExt;
import org.apache.rocketmq.remoting.common.RemotingHelper;

public class TestProducer {

    private DefaultMQProducer producer;

    public TestProducer(String namesrvAddr, String producerGroup) throws MQClientException {
        producer = new DefaultMQProducer(producerGroup);
        producer.setNamesrvAddr(namesrvAddr);
    }

    public void start() throws MQClientException {
        producer.start();
    }

    public SendResult sendMessage(String topic, String tags, String keys, String body) throws Exception {
        Message msg = new Message(topic,
                tags,
                keys,
                body.getBytes(RemotingHelper.DEFAULT_CHARSET));
        return producer.send(msg);
    }

    public QueryResult queryMessage(String topic, String key, int maxNum, long begin, long end) throws Exception {
        return producer.queryMessage(topic, key, maxNum, begin, end);
    }

    public void shutdown() {
        producer.shutdown();
    }
}
