import org.apache.rocketmq.client.producer.DefaultMQProducer;
import org.apache.rocketmq.common.message.Message;
import java.nio.charset.StandardCharsets;

public class RocketMQProducer {

    private DefaultMQProducer producer;
    private String namespace;
    private String producerGroup;
    private String namesrvAddr;
    private String topic;
    private String tag;

    public RocketMQProducer(String namespace, String producerGroup, String namesrvAddr, String topic, String tag) {
        this.namespace = namespace;
        this.producerGroup = producerGroup;
        this.namesrvAddr = namesrvAddr;
        this.topic = topic;
        this.tag = tag;
        this.producer = new DefaultMQProducer(producerGroup);
    }

    public void initialize() throws Exception {
        producer.setNamespaceV2(namespace);
        producer.setNamesrvAddr(namesrvAddr);
        producer.start();
    }

    public void sendMessage(String messageContent) throws Exception {
        Message message = new Message(topic, tag, messageContent.getBytes(StandardCharsets.UTF_8));
        producer.send(message);
    }

    public void shutdown() {
        producer.shutdown();
    }
}
