import java.awt.Image;
import java.awt.image.BufferedImage;
import java.io.*;
import java.net.Socket;
import java.nio.ByteBuffer;
import java.nio.ByteOrder;
import java.nio.IntBuffer;
import java.util.ArrayList;
import java.util.zip.ZipInputStream;

public class DeviceConnection {
    private static final boolean USE_ZIP = true;
    private Socket socket;
    private Reader reader;
    private Writer writer;

    public DeviceConnection() {
        try {
            socket = new Socket("localhost", 4242);
            reader = new Reader(socket.getInputStream());
            writer = new Writer(socket.getOutputStream());
        } catch (IOException e) {
            System.err.println("Did you \"adb forward tcp:4242 tcp:4242\"");
            e.printStackTrace();
        }
    }

    public void close() {
        try {
            if (socket != null) {
                socket.close();
            }
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    public ArrayList<String> listLayoutFiles() {
        ArrayList<String> files = new ArrayList<>();
        if (writer == null) {
            return files;
        }
        writer.println("LIST");
        boolean done = false;
        while (!done) {
            String response = reader.nextLine();
            if (response.equals("DONE")) {
                done = true;
            } else {
                files.add(response);
                System.out.println("Server response: " + response);
            }
        }
        return files;
    }

    public Image takePicture() {
        writer.println("TAKE_PICTURE");
        String response = reader.nextLine();
        int w = Integer.parseInt(response);
        response = reader.nextLine();
        int h = Integer.parseInt(response);
        response = reader.nextLine();
        int size = Integer.parseInt(response);
        if (size == 0) {
            complete();
            return null;
        }
        byte[] bytes = new byte[size];
        BufferedImage image = null;
        try {
            InputStream inputStream = reader.stream;
            int offset = 0;
            while (offset < size) {
                int toRead = size - offset;
                int read = inputStream.read(bytes, offset, toRead);
                if (read > 0) {
                    offset += read;
                }
            }
            if (USE_ZIP) {
                try {
                    ByteArrayInputStream byteArrayInputStream = new ByteArrayInputStream(bytes);
                    ZipInputStream zipInputStream = new ZipInputStream(byteArrayInputStream);
                    zipInputStream.getNextEntry();
                    ByteArrayOutputStream byteArrayOutputStream = new ByteArrayOutputStream();
                    byte[] buff = new byte[32768];
                    int len = 0;
                    while ((len = zipInputStream.read(buff)) > 0) {
                        byteArrayOutputStream.write(buff, 0, len);
                    }
                    zipInputStream.closeEntry();
                    zipInputStream.close();
                    bytes = byteArrayOutputStream.toByteArray();
                } catch(IOException e){
                    e.printStackTrace();
                }
            }

            image = new BufferedImage(w, h, BufferedImage.TYPE_INT_ARGB);
            IntBuffer intBuffer = ByteBuffer.wrap(bytes).order(ByteOrder.LITTLE_ENDIAN).asIntBuffer();
            int[] array = new int[intBuffer.remaining()];
            intBuffer.get(array);
            for (int i = 0; i < array.length; i++) {
                int pixel = array[i];
                int alpha = (pixel >> 24) & 0xff;
                int red = (pixel >> 16) & 0xff;
                int green = (pixel >> 8) & 0xff;
                int blue = (pixel) & 0xff;
                array[i] = (alpha << 24) + (blue << 16) + (green << 8) + red;
            }
            image.getRaster().setDataElements(0, 0, w, h, array);
        } catch (IOException e) {
            e.printStackTrace();
        }
        complete();
        return image;
    }

    private class Reader {
        DataInputStream stream;

        Reader(InputStream stream) {
            this.stream = new DataInputStream(stream);
        }

        String nextLine() {
            try {
                return stream.readUTF();
            } catch (IOException e) {
                e.printStackTrace();
            }
            return "";
        }
    }

    private class Writer {
        DataOutputStream stream;

        Writer(OutputStream stream) {
            this.stream = new DataOutputStream(stream);
        }

        void println(String text) {
            try {
                stream.writeUTF(text);
            } catch (IOException e) {
                e.printStackTrace();
            }
        }
    }

    private void complete() {
        String done = reader.nextLine();
        if (!done.equals("DONE")) {
            System.out.println("WTF!!");
        }
    }
}
