import com.github.ambry.network.NetworkSend;
import com.github.ambry.network.NetworkReceive;
import com.github.ambry.network.PortType;
import com.github.ambry.commons.SSLFactory;
import com.github.ambry.config.NetworkConfig;
import com.github.ambry.utils.Time;
import java.io.IOException;
import java.net.InetSocketAddress;
import java.nio.channels.SelectionKey;
import java.nio.channels.Selector;
import java.nio.channels.SocketChannel;
import java.util.HashMap;
import java.util.Map;

public class NioTcpClient {

  private final java.nio.channels.Selector nioSelector;
  private final Map<String, SelectionKey> keyMap;
  private final Time time;
  private final SSLFactory sslFactory;
  private final NetworkConfig networkConfig;

  public NioTcpClient(Time time, SSLFactory sslFactory, NetworkConfig networkConfig) throws IOException {
    this.nioSelector = java.nio.channels.Selector.open();
    this.time = time;
    this.keyMap = new HashMap<>();
    this.sslFactory = sslFactory;
    this.networkConfig = networkConfig;
  }

  public String connect(InetSocketAddress address, int sendBufferSize, int receiveBufferSize, PortType portType)
      throws IOException {
    SocketChannel channel = SocketChannel.open();
    channel.configureBlocking(false);
    channel.socket().setKeepAlive(true);
    channel.socket().setSendBufferSize(sendBufferSize);
    channel.socket().setReceiveBufferSize(receiveBufferSize);
    channel.socket().setTcpNoDelay(true);
    try {
      channel.connect(address);
    } catch (IOException e) {
      channel.close();
      throw e;
    }
    String connectionId = generateConnectionId(channel);
    SelectionKey key = channel.register(this.nioSelector, SelectionKey.OP_CONNECT);
    Transmission transmission = createTransmission(connectionId, key, address.getHostName(), address.getPort(), portType,
        SSLFactory.Mode.CLIENT);
    key.attach(transmission);
    this.keyMap.put(connectionId, key);
    return connectionId;
  }

  private String generateConnectionId(SocketChannel channel) {
    return channel.socket().getLocalAddress().getHostAddress() + ":" + channel.socket().getLocalPort() + "-" +
        channel.socket().getInetAddress().getHostAddress() + ":" + channel.socket().getPort();
  }

  protected Transmission createTransmission(String connectionId, SelectionKey key, String hostname, int port,
      PortType portType, SSLFactory.Mode mode) throws IOException {
    if (portType == PortType.PLAINTEXT) {
      return new PlainTextTransmission(connectionId, (SocketChannel) key.channel(), key, time, null, networkConfig);
    } else if (portType == PortType.SSL) {
      return new SSLTransmission(sslFactory, connectionId, (SocketChannel) key.channel(), key, hostname, port, time,
          null, mode, networkConfig);
    } else {
      throw new IllegalArgumentException("Unsupported portType " + portType);
    }
  }

  public void disconnect(String connectionId) {
    SelectionKey key = this.keyMap.get(connectionId);
    if (key != null) {
      key.cancel();
    }
  }

  public void close() throws IOException {
    for (SelectionKey key : this.nioSelector.keys()) {
      Transmission transmission = (Transmission) key.attachment();
      if (transmission != null) {
        transmission.close();
      }
      key.channel().close();
    }
    this.nioSelector.close();
  }
}
