import java.net.InetSocketAddress;
import java.net.ServerSocket;
import java.nio.ByteBuffer;
import java.nio.channels.SelectionKey;
import java.nio.channels.Selector;
import java.nio.channels.ServerSocketChannel;
import java.nio.channels.SocketChannel;
import java.util.Date;
import java.util.Iterator;
import java.util.Set;

public class DateServer {

    private Selector selector;
    private ServerSocketChannel[] serverChannels;
    private int[] ports;

    public DateServer(int[] ports) throws Exception {
        this.ports = ports;
        this.selector = Selector.open();
        this.serverChannels = new ServerSocketChannel[ports.length];
        initializeServer();
    }

    private void initializeServer() throws Exception {
        for (int i = 0; i < ports.length; i++) {
            serverChannels[i] = ServerSocketChannel.open();
            serverChannels[i].configureBlocking(false);
            ServerSocket serverSocket = serverChannels[i].socket();
            InetSocketAddress address = new InetSocketAddress(ports[i]);
            serverSocket.bind(address);
            serverChannels[i].register(selector, SelectionKey.OP_ACCEPT);
            System.out.println("服务器运行，在" + ports[i] + "端口监听。");
        }
    }

    public void start() throws Exception {
        int keysAdd = 0;
        while ((keysAdd = selector.select()) > 0) {
            Set<SelectionKey> selectedKeys = selector.selectedKeys();
            Iterator<SelectionKey> iter = selectedKeys.iterator();
            while (iter.hasNext()) {
                SelectionKey key = iter.next();
                if (key.isAcceptable()) {
                    handleAccept(key);
                }
                iter.remove();
            }
        }
    }

    private void handleAccept(SelectionKey key) throws Exception {
        ServerSocketChannel server = (ServerSocketChannel) key.channel();
        SocketChannel client = server.accept();
        client.configureBlocking(false);
        ByteBuffer outBuf = ByteBuffer.allocateDirect(1024);
        outBuf.put(("当前的时间为：" + new Date()).getBytes());
        outBuf.flip();
        client.write(outBuf);
        client.close();
    }

    public void close() throws Exception {
        for (ServerSocketChannel channel : serverChannels) {
            if (channel != null) {
                channel.close();
            }
        }
        if (selector != null) {
            selector.close();
        }
    }
}
