import java.net.InetSocketAddress;
import java.nio.ByteBuffer;
import java.nio.channels.SelectionKey;
import java.nio.channels.Selector;
import java.nio.channels.ServerSocketChannel;
import java.nio.channels.SocketChannel;
import java.util.Iterator;
import java.util.zip.Adler32;
import java.util.zip.Checksum;

public class NIOServer {
    private ServerSocketChannel serverChannel;
    private Selector selector;

    public void start(int port) throws Exception {
        serverChannel = ServerSocketChannel.open();
        serverChannel.configureBlocking(false);
        serverChannel.socket().setReuseAddress(true);
        serverChannel.socket().bind(new InetSocketAddress(port));

        selector = Selector.open();
        serverChannel.register(selector, SelectionKey.OP_ACCEPT);

        while (selector.isOpen()) {
            if (selector.select(3000) == 0) {
                continue;
            }

            Iterator<SelectionKey> it = selector.selectedKeys().iterator();
            while (it.hasNext()) {
                SelectionKey key = it.next();
                it.remove();

                if (!key.isValid()) {
                    continue;
                }

                if (key.isAcceptable()) {
                    handleAccept(key);
                } else if (key.isReadable()) {
                    handleRead(key);
                }
            }
        }
    }

    private void handleAccept(SelectionKey key) throws Exception {
        SocketChannel socketChannel = ((ServerSocketChannel) key.channel()).accept();
        socketChannel.configureBlocking(false);
        socketChannel.register(key.selector(), SelectionKey.OP_READ);
    }

    private void handleRead(SelectionKey key) {
        SocketChannel channel = (SocketChannel) key.channel();
        try {
            ByteBuffer head = ByteBuffer.allocate(4);
            while (!isFull(head)) {
                if (channel.read(head) == -1) {
                    throw new RuntimeException("EOF error");
                }
            }
            head.flip();
            int dataSize = head.getInt();

            ByteBuffer body = ByteBuffer.allocate(dataSize);
            while (!isFull(body)) {
                if (channel.read(body) == -1) {
                    throw new RuntimeException("EOF error");
                }
            }

            ByteBuffer tail = ByteBuffer.allocate(8);
            while (!isFull(tail)) {
                if (channel.read(tail) == -1) {
                    break;
                }
            }
            tail.flip();

            Checksum checksum = new Adler32();
            checksum.update(body.array(), 0, dataSize);
            if (tail.getLong() != checksum.getValue()) {
                throw new RuntimeException("Data corrupted");
            }

            body.flip();
            // Process packet here
        } catch (Exception e) {
            e.printStackTrace();
        } finally {
            try {
                channel.close();
            } catch (Exception ex) {
                ex.printStackTrace();
            }
        }
    }

    private boolean isFull(ByteBuffer buffer) {
        return buffer.position() == buffer.capacity();
    }

    public void stop() {
        try {
            if (selector != null) {
                selector.close();
            }
            if (serverChannel != null) {
                serverChannel.close();
            }
        } catch (Exception e) {
            e.printStackTrace();
        }
    }
}
