import io.vertx.core.Vertx;
import io.vertx.core.net.NetServer;
import io.vertx.core.net.NetServerOptions;
import io.vertx.core.net.NetSocket;
import io.vertx.core.buffer.Buffer;

public class VertxTcpServer {

    private NetServer server;

    public void startServer(Vertx vertx, int port, String host) {
        NetServerOptions options = new NetServerOptions().setPort(port).setHost(host);
        server = vertx.createNetServer(options);

        server.connectHandler(socket -> {
            handleConnection(socket);
        });

        server.listen().onComplete(res -> {
            if (res.succeeded()) {
                System.out.println("Server is now listening on port: " + port);
            } else {
                System.out.println("Failed to bind: " + res.cause().getMessage());
            }
        });
    }

    private void handleConnection(NetSocket socket) {
        socket.handler(buffer -> {
            System.out.println("Received data: " + buffer.length() + " bytes");
            socket.write(Buffer.buffer("Echo: ").appendBuffer(buffer));
        });

        socket.closeHandler(v -> {
            System.out.println("Connection closed");
        });

        socket.exceptionHandler(err -> {
            System.err.println("Connection error: " + err.getMessage());
        });
    }

    public void stopServer() {
        if (server != null) {
            server.close().onComplete(res -> {
                if (res.succeeded()) {
                    System.out.println("Server closed successfully");
                } else {
                    System.err.println("Failed to close server: " + res.cause().getMessage());
                }
            });
        }
    }
}
