import org.apache.kafka.clients.admin.AdminClient;
import org.apache.kafka.clients.admin.AdminClientConfig;
import org.apache.kafka.clients.admin.DescribeConfigsResult;
import org.apache.kafka.clients.admin.Config;
import org.apache.kafka.common.config.ConfigResource;
import java.util.Map;
import java.util.HashMap;
import java.util.Arrays;
import java.util.concurrent.TimeUnit;

public Map<ConfigResource, Config> getKafkaTopicConfigs(String bootstrapServers, String[] topicNames) throws Exception {
    Map<String, Object> adminClientConfig = new HashMap<>();
    adminClientConfig.put(AdminClientConfig.BOOTSTRAP_SERVERS_CONFIG, bootstrapServers);
    AdminClient adminClient = AdminClient.create(adminClientConfig);

    ConfigResource[] configResources = Arrays.stream(topicNames)
        .map(topicName -> new ConfigResource(ConfigResource.Type.TOPIC, topicName))
        .toArray(ConfigResource[]::new);

    DescribeConfigsResult describeConfigsResult = adminClient.describeConfigs(Arrays.asList(configResources));
    return describeConfigsResult.all().get(3, TimeUnit.SECONDS);
}
