import io.grpc.Server;
import io.grpc.ServerBuilder;
import io.grpc.examples.streaming.HelloReply;
import io.grpc.examples.streaming.HelloRequest;
import io.grpc.examples.streaming.StreamingGreeterGrpc;
import io.grpc.stub.StreamObserver;
import java.io.IOException;
import java.util.logging.Logger;

public class HelloWorldServer {
  private static final Logger logger = Logger.getLogger(HelloWorldServer.class.getName());
  private Server server;

  public void start() throws IOException {
    int port = 50051;
    server = ServerBuilder.forPort(port).addService(new GreeterImpl()).build().start();
    logger.info("Server started, listening on " + port);
    Runtime.getRuntime()
        .addShutdownHook(
            new Thread() {
              @Override
              public void run() {
                System.err.println("*** shutting down gRPC server since JVM is shutting down");
                HelloWorldServer.this.stop();
                System.err.println("*** server shut down");
              }
            });
  }

  public void stop() {
    if (server != null) {
      server.shutdown();
    }
  }

  public void blockUntilShutdown() throws InterruptedException {
    if (server != null) {
      server.awaitTermination();
    }
  }

  static class GreeterImpl extends StreamingGreeterGrpc.StreamingGreeterImplBase {
    @Override
    public void sayHelloStreaming(HelloRequest req, StreamObserver<HelloReply> responseObserver) {
      try {
        logger.info("Saying hi");
        responseObserver.onNext(
            HelloReply.newBuilder().setMessage("Hello " + req.getName()).build());
        Thread.sleep(100);
        logger.info("Saying hi");
        responseObserver.onNext(
            HelloReply.newBuilder().setMessage("Hello " + req.getName() + "!").build());
        Thread.sleep(100);
        logger.info("Saying hi");
        responseObserver.onNext(
            HelloReply.newBuilder().setMessage("Hello " + req.getName() + "!!").build());
      } catch (InterruptedException e) {
        responseObserver.onError(e);
      } finally {
        responseObserver.onCompleted();
      }
    }
  }
}
