import java.io.*;
import java.net.*;

public class JankyProxy {
    private static ThreadProxy thread;

    public static void runServer(String host, int remoteport, int localport) {
        try {
            System.out.println("Starting proxy for " + host + ":" + remoteport + " on port " + localport);
            ServerSocket server = new ServerSocket(localport);
            thread = new ThreadProxy(server, host, remoteport);
        } catch (Exception e) {
            System.err.println(e);
            System.err.println("Usage: java ProxyMultiThread <host> <remoteport> <localport>");
        }
    }

    public static boolean wasUsed() {
        return thread.wasUsedForClientToServer;
    }

    public static void shutdown() {
        if (thread != null) {
            try {
                thread.shutdown();
            } catch (Exception e) {
                e.printStackTrace();
            }
        }
    }

    private static class ThreadProxy extends Thread {
        private Socket sClient;
        private ServerSocket socket;
        private final String SERVER_URL;
        private final int SERVER_PORT;
        public boolean wasUsedForClientToServer;

        public ThreadProxy(ServerSocket socket, String ServerUrl, int ServerPort) {
            this.SERVER_URL = ServerUrl;
            this.SERVER_PORT = ServerPort;
            this.socket = socket;
            this.start();
        }

        @Override
        public void run() {
            try {
                sClient = socket.accept();
                final byte[] request = new byte[1024];
                byte[] reply = new byte[4096];
                final InputStream inFromClient = sClient.getInputStream();
                final OutputStream outToClient = sClient.getOutputStream();
                Socket server = null;

                try {
                    server = new Socket(SERVER_URL, SERVER_PORT);
                } catch (IOException e) {
                    PrintWriter out = new PrintWriter(new OutputStreamWriter(outToClient));
                    out.flush();
                    System.out.println("SERVER_URL = " + SERVER_URL + " : " + SERVER_PORT);
                    throw new RuntimeException(e);
                }

                final InputStream inFromServer = server.getInputStream();
                final OutputStream outToServer = server.getOutputStream();

                new Thread(() -> {
                    int bytes_read;
                    try {
                        while ((bytes_read = inFromClient.read(request)) != -1) {
                            outToServer.write(request, 0, bytes_read);
                            outToServer.flush();
                            wasUsedForClientToServer = true;
                        }
                    } catch (IOException ignored) {
                    }
                    try {
                        outToServer.close();
                    } catch (IOException e) {
                        e.printStackTrace();
                    }
                }).start();

                int bytes_read;
                try {
                    while ((bytes_read = inFromServer.read(reply)) != -1) {
                        outToClient.write(reply, 0, bytes_read);
                        outToClient.flush();
                    }
                } catch (IOException e) {
                } finally {
                    try {
                        if (server != null) server.close();
                    } catch (IOException e) {
                        e.printStackTrace();
                    }
                }
                outToClient.close();
                sClient.close();
            } catch (IOException e) {
                e.printStackTrace();
            }
        }

        public void shutdown() {
            try {
                socket.close();
            } catch (IOException e) {
                e.printStackTrace();
            }
            interrupt();
        }
    }
}
