import java.io.BufferedInputStream;
import java.io.BufferedOutputStream;
import java.io.IOException;
import java.io.InputStream;
import java.io.OutputStream;
import java.net.ServerSocket;
import java.net.Socket;

public class Webserver {
    private ServerSocket socket;

    public Webserver(int port) throws IOException {
        socket = new ServerSocket(port);
    }

    public void run() {
        System.err.println("ready to service...");
        for (;;)
            try {
                ServiceThread th = new ServiceThread(this, socket.accept());
                th.start();
            }
            catch (IOException e) {
                System.out.println(e.toString());
            }
    }

    final void process(Socket clnt) throws IOException {
        InputStream in = new BufferedInputStream(clnt.getInputStream());
        OutputStream out = new BufferedOutputStream(clnt.getOutputStream());
        try {
            String cmd = readLine(in);
            while (skipLine(in) > 0) {}
            doReply(in, out, cmd);
        }
        catch (Exception e) {
            replyError(out, e);
        }
        finally {
            out.flush();
            in.close();
            out.close();
            clnt.close();
        }
    }

    private String readLine(InputStream in) throws IOException {
        StringBuilder buf = new StringBuilder();
        int c;
        while ((c = in.read()) >= 0 && c != 0x0d)
            buf.append((char)c);
        in.read();
        return buf.toString();
    }

    private int skipLine(InputStream in) throws IOException {
        int c;
        int len = 0;
        while ((c = in.read()) >= 0 && c != 0x0d)
            ++len;
        in.read();
        return len;
    }

    public void doReply(InputStream in, OutputStream out, String cmd) throws IOException {
        if (cmd.startsWith("GET /")) {
            String filename = cmd.substring(5, cmd.indexOf(' ', 5));
            out.write("HTTP/1.0 200 OK\r\n\r\n".getBytes());
        }
        else
            throw new IOException("Bad Request");
    }

    private void replyError(OutputStream out, Exception e) throws IOException {
        out.write("HTTP/1.0 400 Bad Request\r\n\r\n".getBytes());
        out.write("<H1>Bad Request</H1>".getBytes());
    }
}

class ServiceThread extends Thread {
    Webserver web;
    Socket sock;

    public ServiceThread(Webserver w, Socket s) {
        web = w;
        sock = s;
    }

    @Override
    public void run() {
        try {
            web.process(sock);
        }
        catch (IOException e) {
        }
    }
}
