import java.io.BufferedInputStream;
import java.io.BufferedOutputStream;
import java.io.IOException;
import java.net.InetAddress;
import java.net.InetSocketAddress;
import java.net.Socket;
import java.net.UnknownHostException;

class Pop3Connection {
    private Socket socket;
    private BufferedInputStream in;
    private BufferedOutputStream out;

    private Socket connect() throws IOException, UnknownHostException {
        InetAddress[] inetAddresses = InetAddress.getAllByName(settings.getHost());
        IOException connectException = null;
        for (InetAddress address : inetAddresses) {
            try {
                return connectToAddress(address);
            } catch (IOException e) {
                connectException = e;
            }
        }
        throw connectException != null ? connectException : new UnknownHostException();
    }

    private Socket connectToAddress(InetAddress address) throws IOException {
        InetSocketAddress socketAddress = new InetSocketAddress(address, settings.getPort());
        final Socket socket = new Socket();
        socket.connect(socketAddress, SOCKET_CONNECT_TIMEOUT);
        return socket;
    }

    void open() throws MessagingException {
        try {
            socket = connect();
            in = new BufferedInputStream(socket.getInputStream(), 1024);
            out = new BufferedOutputStream(socket.getOutputStream(), 512);
            socket.setSoTimeout(SOCKET_READ_TIMEOUT);
            if (!isOpen()) {
                throw new MessagingException("Unable to connect socket");
            }
        } catch (IOException ioe) {
            close();
            throw new MessagingException("Unable to open connection to POP server.", ioe);
        }
    }

    boolean isOpen() {
        return (in != null && out != null && socket != null
                && socket.isConnected() && !socket.isClosed());
    }

    void close() {
        try {
            in.close();
        } catch (Exception e) {}
        try {
            out.close();
        } catch (Exception e) {}
        try {
            socket.close();
        } catch (Exception e) {}
        in = null;
        out = null;
        socket = null;
    }

    private void writeLine(String s) throws IOException {
        out.write(s.getBytes());
        out.write('\r');
        out.write('\n');
        out.flush();
    }

    String readLine() throws IOException {
        StringBuilder sb = new StringBuilder();
        int d = in.read();
        if (d == -1) {
            throw new IOException("End of stream reached while trying to read line.");
        }
        do {
            if (((char)d) == '\r') {
                continue;
            } else if (((char)d) == '\n') {
                break;
            } else {
                sb.append((char)d);
            }
        } while ((d = in.read()) != -1);
        return sb.toString();
    }
}
