import java.net.DatagramSocket;
import java.net.DatagramPacket;
import java.net.InetAddress;
import java.net.SocketException;
import java.io.IOException;

public class SnmpUdpHandler {

    private DatagramSocket socket;
    private int bufferSize = 1024;
    private InetAddress address;
    private int port;

    public SnmpUdpHandler(int port, InetAddress address) throws SocketException {
        this.port = port;
        this.address = address;
        this.socket = new DatagramSocket(port, address);
    }

    public void sendPacket(byte[] data, InetAddress destination, int destPort) throws IOException {
        DatagramPacket packet = new DatagramPacket(data, data.length, destination, destPort);
        socket.send(packet);
    }

    public byte[] receivePacket() throws IOException {
        byte[] buffer = new byte[bufferSize];
        DatagramPacket packet = new DatagramPacket(buffer, buffer.length);
        socket.receive(packet);
        return packet.getData();
    }

    public void close() {
        if (socket != null) {
            socket.close();
        }
    }

    public int getPort() {
        return socket != null ? socket.getLocalPort() : port;
    }

    public void setBufferSize(int size) {
        this.bufferSize = size;
    }
}
