import android.content.SharedPreferences;
import android.os.Bundle;
import android.view.View;
import android.view.View.OnClickListener;
import android.widget.Button;
import android.widget.CheckBox;
import android.widget.EditText;
import android.widget.Spinner;
import android.widget.Toast;
import android.widget.ToggleButton;

import org.csploit.android.R;
import org.csploit.android.core.Plugin;
import org.csploit.android.core.System;
import org.csploit.android.net.Target;
import org.csploit.android.net.Target.Type;

import java.net.DatagramPacket;
import java.net.DatagramSocket;

public class UDPClient {

    private DatagramSocket mUdpSocket = null;
    private boolean mRunning = false;
    private Thread mThread = null;

    public void sendUDPPacket(String targetAddress, int port, String data, boolean waitResponse, final ResponseHandler handler) {
        if (!mRunning) {
            mRunning = true;

            mThread = new Thread(new Runnable() {
                @Override
                public void run() {
                    String error = null;
                    try {
                        mUdpSocket = new DatagramSocket();
                        DatagramPacket packet = new DatagramPacket(
                                data.getBytes(), data.length(),
                                System.getCurrentTarget().getAddress(), port);

                        mUdpSocket.send(packet);

                        if (waitResponse) {
                            byte[] buffer = new byte[1024];
                            DatagramPacket response = new DatagramPacket(buffer, buffer.length);
                            mUdpSocket.receive(response);
                            final String responseText = new String(buffer);
                            handler.onResponse(responseText);
                        }
                    } catch (Exception e) {
                        error = e.getMessage();
                    } finally {
                        if (mUdpSocket != null) {
                            mUdpSocket.close();
                        }
                        mRunning = false;
                        if (error != null) {
                            handler.onError(error);
                        }
                    }
                }
            });

            mThread.start();
        }
    }

    public void stop() {
        mRunning = false;
        try {
            if (mThread != null && mThread.isAlive()) {
                if (mUdpSocket != null)
                    mUdpSocket.close();
                mThread.interrupt();
                mThread = null;
            }
        } catch (Exception e) {
            // Ignore
        }
    }

    public interface ResponseHandler {
        void onResponse(String response);
        void onError(String errorMessage);
    }
}
