import java.net.DatagramPacket;
import java.net.DatagramSocket;
import java.net.InetAddress;

public class UdpCommunicationHandler {

    private DatagramSocket mDatagramSocket;

    public void startFileReceiverServer(int serverPort) throws Exception {
        mDatagramSocket = new DatagramSocket(serverPort);
        byte[] receiveData = new byte[1024];

        while(true) {
            DatagramPacket receivePacket = new DatagramPacket(receiveData, receiveData.length);
            mDatagramSocket.receive(receivePacket);
            String msg = new String(receivePacket.getData()).trim();
            InetAddress inetAddress = receivePacket.getAddress();
            int port = receivePacket.getPort();

            if(msg != null && msg.startsWith(Constant.MSG_FILE_RECEIVER_INIT)) {
                handleReceiverInit(inetAddress, port);
            } else {
                parseFileInfo(msg);
            }
        }
    }

    private void handleReceiverInit(InetAddress inetAddress, int port) {
        // Handle receiver initialization logic
    }

    private void parseFileInfo(String msg) {
        // Parse file information
    }

    public void closeSocket() {
        if(mDatagramSocket != null) {
            mDatagramSocket.disconnect();
            mDatagramSocket.close();
            mDatagramSocket = null;
        }
    }
}
