import android.util.Log;
import java.net.DatagramPacket;
import java.net.DatagramSocket;
import java.net.InetAddress;

public class UdpCommunicationHandler {
    private DatagramSocket mDatagramSocket;

    public void sendFileReceiverInitSuccessMsg(IpPortInfo ipPortInfo) throws Exception {
        Log.i("UdpCommunicationHandler", "sendFileReceiverInitSuccessMsg------>>>start");
        mDatagramSocket = new DatagramSocket(ipPortInfo.getPort() + 1);
        byte[] sendData = Constant.MSG_FILE_RECEIVER_INIT_SUCCESS.getBytes(BaseTransfer.UTF_8);
        DatagramPacket sendPacket = new DatagramPacket(
            sendData, 
            sendData.length, 
            ipPortInfo.getInetAddress(), 
            ipPortInfo.getPort()
        );
        mDatagramSocket.send(sendPacket);
        Log.i("UdpCommunicationHandler", "Send Msg To FileSender######>>>" + Constant.MSG_FILE_RECEIVER_INIT_SUCCESS);
        Log.i("UdpCommunicationHandler", "sendFileReceiverInitSuccessMsg------>>>end");
    }

    public void closeSocket() {
        if (mDatagramSocket != null) {
            mDatagramSocket.disconnect();
            mDatagramSocket.close();
            mDatagramSocket = null;
        }
    }
}
