import android.os.AsyncTask;
import java.io.BufferedInputStream;
import java.io.InputStream;
import java.net.HttpURLConnection;
import java.net.URL;

public class HttpRequestHandler {

    public interface HttpRequestCallback {
        void onProgressUpdate(String... progress);
        void onPostExecute(String result);
    }

    public static class MyAsyncTaskgetNews extends AsyncTask<String, String, String> {
        private HttpRequestCallback callback;

        public MyAsyncTaskgetNews(HttpRequestCallback callback) {
            this.callback = callback;
        }

        @Override
        protected String doInBackground(String... params) {
            try {
                URL url = new URL(params[0]);
                HttpURLConnection urlConnection = (HttpURLConnection) url.openConnection();
                urlConnection.setConnectTimeout(7000);

                try {
                    InputStream in = new BufferedInputStream(urlConnection.getInputStream());
                    Operations operations = new Operations(); // Context would need to be passed if needed
                    String NewsData = operations.ConvertInputToStringNoChange(in);
                    publishProgress(NewsData);
                } finally {
                    urlConnection.disconnect();
                }
            } catch (Exception ex) {
                ex.printStackTrace();
            }
            return null;
        }

        @Override
        protected void onProgressUpdate(String... progress) {
            if (callback != null) {
                callback.onProgressUpdate(progress);
            }
        }

        @Override
        protected void onPostExecute(String result) {
            if (callback != null) {
                callback.onPostExecute(result);
            }
        }
    }
}
