const http = require('http');
const send = require('send');
const marked = require('marked');
const fs = require('fs');
const path = require('path');

class Server {
  constructor(config) {
    this.config = config || {};
    this._http = null;
    this.sockets = [];
    this.path = this.config.path || '';

    this.updateServer();
  }

  updateServer() {
    marked.setOptions({
      gfm: true,
      tables: true,
      breaks: false,
      pedantic: false,
      sanitize: true,
      smartLists: true,
      smartypants: false
    });

    const html = fs.readFileSync(path.join('lib', 'public', 'directory.html'));

    this._http = http.createServer((req, res) => {
      function redirect() {
        res.statusCode = 301;
        res.setHeader('Location', req.url + '/');
        res.end('Redirecting to ' + req.url + '/');
      }

      function procError(err) {
        if (err.message.indexOf('ENOENT') >= 0) {
          const p = path.dirname(err.message.match(/'(.*?)'/)[1]) || '';
          const bn = path.basename(err.message.match(/'(.*?)'/)[1]) || '';

          if (bn === 'index.html') {
            fs.readdir(p, (err, files) => {
              if (err) {
                res.statusCode = 500;
                res.end();
                return;
              }

              const dirs = files.filter(el => fs.statSync(path.join(p, el)).isDirectory())
                .map(el => {
                  const stat = fs.statSync(path.join(p, el));
                  return `<tr class='dir'>
                    <td><a href="./${el}/">${el}</a></td>
                    <td>&lt;Directory&gt;</td>
                    <td>&lt;Directory&gt;</td>
                    <td>${stat.mtime}</td>
                  </tr>`;
                });

              const filesList = files.filter(el => !fs.statSync(path.join(p, el)).isDirectory())
                .map(el => {
                  const stat = fs.statSync(path.join(p, el));
                  const ext = path.extname(el).substr(1);
                  return `<tr class='file'>
                    <td><a href="./${el}">${el}</a></td>
                    <td>${ext.toUpperCase()} File</td>
                    <td>${(stat.size/1024).toFixed(2)} KB</td>
                    <td>${stat.mtime}</td>
                  </tr>`;
                });

              res.statusCode = 200;
              res.end(html.toString().replace('<!-- BODY -->', dirs.join('') + filesList.join('')));
            });
            return;
          }
        }

        res.statusCode = err.status || 500;
        res.end(err.message);
      }

      try {
        send(req, require('url').parse(req.url).pathname)
          .from(this.path)
          .on('error', procError)
          .on('directory', redirect)
          .pipe(res);
      } catch (e) {
        procError(e);
      }
    });

    this._http.on('connection', (socket) => {
      this.sockets.push(socket);
      socket.setTimeout(4000);
      socket.on('close', () => {
        this.sockets.splice(this.sockets.indexOf(socket), 1);
      });
    });
  }

  start(callback) {
    this._http.listen(this.config.port, () => {
      callback && callback();
    });
  }

  stop(callback) {
    this.sockets.forEach(socket => socket.destroy());
    this._http.close(() => {
      callback && callback();
    });
  }
}
