var events = require('events');
var net = require('net');

class TCPListener {
    constructor(port) {
        this.port = port;
        this.server = null;
    }

    start() {
        this.server = net.createServer((c) => {
            var data = '';
            try {
                c.on('data', (d) => {
                    data += d;
                    if (data.slice(-1) === '\n') {
                        var message = JSON.parse(data);
                        this.emit('command', message.command, message.params, message.options, (message) => {
                            c.end(message);
                        });
                    }
                });
                c.on('end', () => {});
                c.on('error', () => {});
            }
            catch(e) {
                this.emit('log', 'CLI listener failed to parse message ' + data);
            }
        }).listen(this.port, '127.0.0.1', () => {
            this.emit('log', 'CLI listening on port ' + this.port);
        });
    }
}

TCPListener.prototype.__proto__ = events.EventEmitter.prototype;
