var request = require('request');
var nonce = require('nonce');
var crypto = require('crypto');

class Cryptsy {
    constructor(key, secret) {
        this.key = key;
        this.secret = secret;
        this.PUBLIC_API_URL = 'http://pubapi.cryptsy.com/api.php';
        this.PRIVATE_API_URL = 'https://api.cryptsy.com/api';
        this.USER_AGENT = 'nomp/node-open-mining-portal';
    }

    static STRICT_SSL = true;

    _getPrivateHeaders(parameters) {
        if (!this.key || !this.secret) {
            throw 'Cryptsy: Error. API key and secret required';
        }

        let paramString = Object.keys(parameters).sort().map(function(param) {
            return encodeURIComponent(param) + '=' + encodeURIComponent(parameters[param]);
        }).join('&');

        let signature = crypto.createHmac('sha512', this.secret).update(paramString).digest('hex');

        return {
            Key: this.key,
            Sign: signature
        };
    }

    _request(options, callback) {
        if (!('headers' in options)) {
            options.headers = {};
        }

        options.headers['User-Agent'] = this.USER_AGENT;
        options.json = true;
        options.strictSSL = Cryptsy.STRICT_SSL;

        request(options, function(err, response, body) {
            callback(err, body);
        });

        return this;
    }

    _public(parameters, callback) {
        let options = {
            method: 'GET',
            url: this.PUBLIC_API_URL,
            qs: parameters
        };

        return this._request(options, callback);
    }

    _private(parameters, callback) {
        parameters.nonce = nonce();
        let options = {
            method: 'POST',
            url: this.PRIVATE_API_URL,
            form: parameters,
            headers: this._getPrivateHeaders(parameters)
        };

        return this._request(options, callback);
    }

    getTicker(callback) {
        let parameters = {
            method: 'marketdatav2'
        };
        return this._public(parameters, callback);
    }

    getOrderBook(currencyA, currencyB, callback) {
        let parameters = {
            command: 'returnOrderBook',
            currencyPair: this._joinCurrencies(currencyA, currencyB)
        };
        return this._public(parameters, callback);
    }

    getTradeHistory(currencyA, currencyB, callback) {
        let parameters = {
            command: 'returnTradeHistory',
            currencyPair: this._joinCurrencies(currencyA, currencyB)
        };
        return this._public(parameters, callback);
    }

    myBalances(callback) {
        let parameters = {
            command: 'returnBalances'
        };
        return this._private(parameters, callback);
    }

    myOpenOrders(currencyA, currencyB, callback) {
        let parameters = {
            command: 'returnOpenOrders',
            currencyPair: this._joinCurrencies(currencyA, currencyB)
        };
        return this._private(parameters, callback);
    }

    myTradeHistory(currencyA, currencyB, callback) {
        let parameters = {
            command: 'returnTradeHistory',
            currencyPair: this._joinCurrencies(currencyA, currencyB)
        };
        return this._private(parameters, callback);
    }

    buy(currencyA, currencyB, rate, amount, callback) {
        let parameters = {
            command: 'buy',
            currencyPair: this._joinCurrencies(currencyA, currencyB),
            rate: rate,
            amount: amount
        };
        return this._private(parameters, callback);
    }

    sell(currencyA, currencyB, rate, amount, callback) {
        let parameters = {
            command: 'sell',
            currencyPair: this._joinCurrencies(currencyA, currencyB),
            rate: rate,
            amount: amount
        };
        return this._private(parameters, callback);
    }

    cancelOrder(currencyA, currencyB, orderNumber, callback) {
        let parameters = {
            command: 'cancelOrder',
            currencyPair: this._joinCurrencies(currencyA, currencyB),
            orderNumber: orderNumber
        };
        return this._private(parameters, callback);
    }

    withdraw(currency, amount, address, callback) {
        let parameters = {
            command: 'withdraw',
            currency: currency,
            amount: amount,
            address: address
        };
        return this._private(parameters, callback);
    }

    _joinCurrencies(currencyA, currencyB) {
        return currencyA + '_' + currencyB;
    }
}
