class N {
  constructor(url, options) {
    this.url = url;
    this.options = options;
    this.socket = null;
    this.streaming = true;
    this.callbacks = { connect: [], data: [] };
    this.destination = null;
    this.reconnectInterval = void 0 !== options.reconnectInterval ? options.reconnectInterval : 5;
    this.shouldAttemptReconnect = !!this.reconnectInterval;
    this.completed = false;
    this.established = false;
    this.progress = 0;
    this.reconnectTimeoutId = 0;
    this.onEstablishedCallback = options.onSourceEstablished;
    this.onCompletedCallback = options.onSourceCompleted;
    options.hookOnEstablished && (this.hookOnEstablished = options.hookOnEstablished);
  }

  connect(destination) {
    this.destination = destination;
  }

  destroy() {
    clearTimeout(this.reconnectTimeoutId);
    this.shouldAttemptReconnect = false;
    this.socket.close();
  }

  start() {
    this.shouldAttemptReconnect = !!this.reconnectInterval;
    this.progress = 0;
    this.established = false;
    this.options.protocols
      ? this.socket = new WebSocket(this.url, this.options.protocols)
      : this.socket = new WebSocket(this.url);
    this.socket.binaryType = "arraybuffer";
    this.socket.onmessage = this.onMessage.bind(this);
    this.socket.onopen = this.onOpen.bind(this);
    this.socket.onerror = this.onClose.bind(this);
    this.socket.onclose = this.onClose.bind(this);
  }

  resume() {}

  onOpen() {
    this.progress = 1;
  }

  onClose() {
    const reconnect = () => {
      clearTimeout(this.reconnectTimeoutId);
      this.reconnectTimeoutId = setTimeout(() => {
        this.start();
      }, 1000 * this.reconnectInterval);
    };
    this.shouldAttemptReconnect && reconnect();
  }

  onMessage(message) {
    const wasEstablished = this.established;
    this.established = true;
    wasEstablished || this.hookOnEstablished?.();
    wasEstablished || this.onEstablishedCallback?.(this);
    this.destination?.write(message.data);
  }
}
