var wsByteArrayUri = "ws://" + document.location.host + document.location.pathname + "bytearray";
var wsByteBufferUri = "ws://" + document.location.host + document.location.pathname + "bytebuffer";
var wsInputStreamUri = "ws://" + document.location.host + document.location.pathname + "inputstream";

class WebSocketClient {
    constructor() {
        this.websocketByteArray = new WebSocket(wsByteArrayUri);
        this.websocketByteBuffer = new WebSocket(wsByteBufferUri);
        this.websocketInputStream = new WebSocket(wsInputStreamUri);

        this.websocketByteArray.binaryType = "arraybuffer";
        this.websocketByteBuffer.binaryType = "arraybuffer";
        this.websocketInputStream.binaryType = "arraybuffer";

        this.output = document.getElementById("output");

        this.initializeEventHandlers();
    }

    initializeEventHandlers() {
        this.websocketByteArray.onopen = (evt) => this.onOpenByteArray(evt);
        this.websocketByteArray.onmessage = (evt) => this.onMessageByteArray(evt);
        this.websocketByteArray.onerror = (evt) => this.onError(evt);

        this.websocketByteBuffer.onopen = (evt) => this.onOpenByteBuffer(evt);
        this.websocketByteBuffer.onmessage = (evt) => this.onMessageByteBuffer(evt);
        this.websocketByteBuffer.onerror = (evt) => this.onError(evt);

        this.websocketInputStream.onopen = (evt) => this.onOpenInputStream(evt);
        this.websocketInputStream.onmessage = (evt) => this.onMessageInputStream(evt);
        this.websocketInputStream.onerror = (evt) => this.onError(evt);
    }

    onOpenByteArray() {
        console.log("onOpen (byte])");
    }

    onOpenByteBuffer() {
        console.log("onOpen (ByteBuffer)");
    }

    onOpenInputStream() {
        console.log("onOpen (InputStream)");
    }

    echoBinaryByteArray() {
        var buffer = new ArrayBuffer(myField.value.length);
        var bytes = new Uint8Array(buffer);
        for (var i=0; i<bytes.length; i++) {
            bytes[i] = i;
        }
        this.websocketByteArray.send(buffer);
        this.writeToScreen("SENT (byte[]): " + buffer.byteLength + " bytes");
    }

    echoBinaryByteBuffer() {
        var buffer = new ArrayBuffer(myField.value.length);
        var bytes = new Uint8Array(buffer);
        for (var i=0; i<bytes.length; i++) {
            bytes[i] = i;
        }
        this.websocketByteBuffer.send(buffer);
        this.writeToScreen("SENT (ByteBuffer): " + buffer.byteLength + " bytes");
    }

    echoBinaryInputStream() {
        var buffer = new ArrayBuffer(myField.value.length);
        var bytes = new Uint8Array(buffer);
        for (var i=0; i<bytes.length; i++) {
            bytes[i] = i;
        }
        this.websocketInputStream.send(buffer);
        this.writeToScreen("SENT (InputStream): " + buffer.byteLength + " bytes");
    }

    onMessageByteArray(evt) {
        this.writeToScreen("RECEIVED (byte[]): " + evt.data);
    }

    onMessageByteBuffer(evt) {
        this.writeToScreen("RECEIVED (ByteBuffer): " + evt.data);
    }

    onMessageInputStream(evt) {
        this.writeToScreen("RECEIVED (InputStream): " + evt.data);
    }

    onError(evt) {
        this.writeToScreen('<span style="color: red;">ERROR:</span> ' + evt.data);
    }

    writeToScreen(message) {
        var pre = document.createElement("p");
        pre.style.wordWrap = "break-word";
        pre.innerHTML = message;
        this.output.appendChild(pre);
    }
}
