var request = require('request');

class SauceLabsClient {
  constructor() {
    this.auth = {
      user: null,
      password: null
    };
  }

  setAuth(saucelabsUsername, saucelabsToken) {
    this.auth.user = saucelabsUsername;
    this.auth.password = saucelabsToken;
  }

  get(path, callback) {
    var params = {
      url: 'https://saucelabs.com/rest/v1/' + path,
      auth: this.auth
    };
    request.get(params, function(err, res, body) {
      if (err) {
        throw err;
      }
      if (res.statusCode != 200) {
        console.log(params);
        throw new Error('Non-200 status code: ' + body);
      }
      callback(JSON.parse(body));
    });
  }

  post(path, data, callback) {
    var params = {
      url: 'https://saucelabs.com/rest/v1/' + this.auth.user + '/' + path,
      auth: { user: this.auth.user, password: this.auth.password },
      json: data
    };
    request.post(params, function(err, res, body) {
      if (err) {
        throw err;
      }
      if (res.statusCode != 200) {
        throw new Error('Non-200 status code: ' + body);
      }
      callback(body);
    });
  }
}
