var jQuery = require("../core");
var support = require("../var/support");

class AjaxTransport {
  constructor() {
    this.xhrId = 0;
    this.xhrCallbacks = {};
    this.xhrSuccessStatus = {
      0: 200,
      1223: 204
    };
    this.xhrSupported = this.createXHR();

    if (window.ActiveXObject) {
      jQuery(window).on("unload", () => {
        for (var key in this.xhrCallbacks) {
          this.xhrCallbacks[key]();
        }
      });
    }

    support.cors = !!this.xhrSupported && ("withCredentials" in this.xhrSupported);
    support.ajax = !!this.xhrSupported;
  }

  createXHR() {
    try {
      return new XMLHttpRequest();
    } catch(e) {}
  }

  sendRequest(options, headers, complete) {
    var xhr = options.xhr();
    var id = ++this.xhrId;

    xhr.open(options.type, options.url, options.async, options.username, options.password);

    if (options.xhrFields) {
      for (var i in options.xhrFields) {
        xhr[i] = options.xhrFields[i];
      }
    }

    if (options.mimeType && xhr.overrideMimeType) {
      xhr.overrideMimeType(options.mimeType);
    }

    if (!options.crossDomain && !headers["X-Requested-With"]) {
      headers["X-Requested-With"] = "XMLHttpRequest";
    }

    for (var i in headers) {
      xhr.setRequestHeader(i, headers[i]);
    }

    var callback = (type) => {
      return () => {
        if (callback) {
          delete this.xhrCallbacks[id];
          callback = xhr.onload = xhr.onerror = null;

          if (type === "abort") {
            xhr.abort();
          } else if (type === "error") {
            complete(xhr.status, xhr.statusText);
          } else {
            complete(
              this.xhrSuccessStatus[xhr.status] || xhr.status,
              xhr.statusText,
              typeof xhr.responseText === "string" ? { text: xhr.responseText } : undefined,
              xhr.getAllResponseHeaders()
            );
          }
        }
      };
    };

    xhr.onload = callback();
    xhr.onerror = callback("error");
    callback = this.xhrCallbacks[id] = callback("abort");

    try {
      xhr.send(options.hasContent && options.data || null);
    } catch (e) {
      if (callback) {
        throw e;
      }
    }
  }

  abortRequest(callback) {
    if (callback) {
      callback();
    }
  }

  getTransport(options) {
    if (support.cors || this.xhrSupported && !options.crossDomain) {
      return {
        send: (headers, complete) => this.sendRequest(options, headers, complete),
        abort: () => this.abortRequest(callback)
      };
    }
  }
}
