const express = require('express');
const bodyParser = require('body-parser');
const cookieParser = require('cookie-parser');
const multer = require("multer");

class HTTPServer {
    constructor(port) {
        this.app = express();
        this.port = port;
        this.server = null;

        // Configure middleware
        this.app.use(bodyParser.raw({type:"*/*"}));
        this.app.use(cookieParser(undefined,{decode:String}));

        // File upload handling
        const fileUploadApp = express();
        const mp = multer({ storage: multer.memoryStorage() }).any();
        fileUploadApp.post("/file-upload",function(req,res,next) {
            mp(req,res,function(err) {
                req._body = true;
                next(err);
            })
        },bodyParser.json(),function(req,res) {
            res.json({
                body: req.body,
                files: req.files
            })
        });
        this.app.use(fileUploadApp);
    }

    start() {
        return new Promise((resolve, reject) => {
            this.server = this.app.listen(this.port, err => {
                if (err) {
                    return reject(err);
                }
                resolve();
            });
        });
    }

    stop() {
        if (this.server) {
            this.server.close();
        }
    }

    addRoute(method, path, handler) {
        this.app[method.toLowerCase()](path, handler);
    }

    get(path, handler) {
        this.addRoute('get', path, handler);
    }

    post(path, handler) {
        this.addRoute('post', path, handler);
    }

    put(path, handler) {
        this.addRoute('put', path, handler);
    }

    delete(path, handler) {
        this.addRoute('delete', path, handler);
    }

    patch(path, handler) {
        this.addRoute('patch', path, handler);
    }

    options(path, handler) {
        this.addRoute('options', path, handler);
    }

    trace(path, handler) {
        this.addRoute('trace', path, handler);
    }

    head(path, handler) {
        this.addRoute('head', path, handler);
    }
}
