import stomp
from typing import List

class MessageArtemisExtension:
    conns = []

    def __init__(self):
        self.conn = None

    def listen_queue(self, profile: dict):
        if not profile:
            return

        self.conn = stomp.Connection(
            host_and_ports=[
                (
                    profile.get("host","127.0.0.1"), 
                    profile.get("port",61616)
                )
            ],
            heartbeats=(6000, 12000)
        )
        self.conn.set_listener('', MessageListener())
        self.conn.connect(
            profile.get("username","artemis"), 
            profile.get("password","artemis"), 
            wait=True,
            headers={"client-id": "arkid"},
        )
        self.conn.subscribe(
            destination=profile.get("destination","arkid"),
            id=profile.get("destination","arkid"),
            ack="auto",
            headers={
                'subscription-type': 'ANYCAST',
                'durable-subscription-name': f'arkid.{profile.get("destination","arkid")}',
            },
        )
