from fastapi import FastAPI
from pydantic import BaseModel, Field
from starlette.responses import StreamingResponse
from typing import List, Dict, Any

class Message(BaseModel):
    role: str = Field(
        ..., description="The role of the author of the message, typically 'user', or 'assistant'."
    )
    content: str = Field(..., description="The content of the message.")

class Request(BaseModel):
    messages: List[Message] = Field(
        ..., description="A list of messages that make up the conversation."
    )

class Query(BaseModel):
    query: str

class Answer(BaseModel):
    question: str
    answer: str
    sources: List[str]
    llm: str

class RayAssistantDeployment:
    def __init__(self, app: FastAPI):
        self.app = app
        self.app.post("/query")(self.query)
        self.app.post("/chat")(self.chat)

    def predict(self, query: Query, stream: bool) -> Dict[str, Any]:
        # Implementation would go here
        pass

    def query(self, query: Query) -> Answer:
        result = self.predict(query, stream=False)
        return Answer.parse_obj(result)

    def produce_chat_answer(self, request: Request, result: Dict[str, Any]):
        answer = []
        for answer_piece in result["answer"]:
            answer.append(answer_piece)
            yield answer_piece

        if result["sources"]:
            yield "\n\n**Sources:**\n"
            for source in result["sources"]:
                yield "* " + source + "\n"

    def chat(self, request: Request) -> StreamingResponse:
        if len(request.messages) == 1:
            query = Query(query=request.messages[0].content)
            result = self.predict(query, stream=True)
        else:
            # Simplified implementation for follow-up questions
            result = {"answer": ["Response"], "llm": "gpt-4", "sources": [], "document_ids": []}

        return StreamingResponse(
            self.produce_chat_answer(request, result), media_type="text/plain"
        )
