from flask import Flask, request, make_response
import logging
from faker import Faker
from typing import Dict, Any

class SubscriptionServer:
    def __init__(self, app_name: str = "web", logger: logging.Logger = None):
        self.app = Flask(app_name)
        self.logger = logger if logger else logging.getLogger()
        self.fake = Faker()

        # Configure logger
        for handler in self.app.logger.handlers:
            self.app.logger.removeHandler(handler)
        for handler in self.logger.handlers:
            self.app.logger.addHandler(handler)
        self.app.logger.setLevel(self.logger.level)

        self._setup_routes()

    def _setup_routes(self):
        @self.app.route('/sub', methods=['GET'])
        def httpAutoSub():
            user_agent = request.headers.get('User-Agent', 'unknown').lower()
            sub_type = getSubTypeFromUA(user_agent)
            return self._handle_subscription(sub_type)

        @self.app.route('/api/<string:sub_type>', methods=['GET'])
        def httpSubscription(sub_type: str):
            return self._handle_subscription(sub_type)

    def _handle_subscription(self, sub_type: str) -> Any:
        user_agent = request.headers.get('User-Agent', 'unknown').lower()
        account = getCurrentAccount(self.logger)
        best = request.args.get('best', 'false').lower() == "true" or False
        random_name = request.args.get('randomName', 'false').lower() == "true" or False
        proxy_format = request.args.get('proxyFormat', 'full').lower()
        ipv6 = request.args.get('ipv6', 'false').lower() == "true" or False

        headers = {
            'Content-Type': 'application/x-yaml; charset=utf-8',
            "Subscription-Userinfo": f"upload=0; download={account.usage}; total={account.quota}; "
                                    f"expire=253388144714"
        }

        is_android = "android" in user_agent

        if sub_type == "clash":
            file_data = generateClashSubFile(account, self.logger, best=best, proxy_format=proxy_format,
                                           random_name=random_name, is_android=is_android, is_meta=False, ipv6=ipv6)
            file_name = f'Clash-{self.fake.color_name()}.yaml'
        elif sub_type == "meta":
            file_data = generateClashSubFile(account, self.logger, best=best, proxy_format=proxy_format,
                                           random_name=random_name, is_android=is_android, is_meta=True, ipv6=ipv6)
            file_name = f'Clash-{self.fake.color_name()}.yaml'
        elif sub_type == "wireguard":
            file_data = generateWireguardSubFile(account, self.logger, best=best, ipv6=ipv6)
            file_name = f'WireGuard-{self.fake.lexify("????????????").lower()}.conf'
        elif sub_type == "surge":
            file_data = generateSurgeSubFile(account, self.logger, best=best, random_name=random_name,
                                           proxy_format=proxy_format, ipv6=ipv6)
            file_name = f'Surge-{self.fake.color_name()}.conf'
        elif sub_type == 'shadowrocket':
            file_data = generateShadowRocketSubFile(account, self.logger, best=best, random_name=random_name, ipv6=ipv6)
            file_name = f'Shadowrocket-{self.fake.color_name()}.conf'
        elif sub_type == 'sing-box':
            file_data = generateSingBoxSubFile(account, self.logger, best=best, random_name=random_name, ipv6=ipv6)
            file_name = f'SingBox-{self.fake.color_name()}.json'
        elif sub_type == 'loon':
            file_data = generateLoonSubFile(account, self.logger, best=best, random_name=random_name, ipv6=ipv6)
            file_name = f'Loon-{self.fake.color_name()}.conf'
        elif sub_type == "only_proxies":
            file_data = generateClashSubFile(account, self.logger, best=best, proxy_format='with_groups',
                                           random_name=random_name, ipv6=ipv6)
            file_name = f'Clash-{self.fake.color_name()}.yaml'
        else:
            return {
                'code': 400,
                'message': 'Unsupported sub type.'
            }, 400

        headers['Content-Disposition'] = f'attachment; filename="{file_name}"'
        response = make_response(file_data)
        response.headers = headers
        return response

    def run(self, host: str = '0.0.0.0', port: int = 5000, debug: bool = True):
        self.app.run(host=host, port=port, debug=debug)
