from flask import Flask, Response, stream_with_context, request
from flask_cors import CORS, cross_origin
import json
import concurrent.futures
import time

class ModelServer:
    def __init__(self, model):
        self.app = Flask(__name__)
        CORS(self.app)
        self.model = model

        @self.app.route('/stream', methods=['GET', 'POST'])
        @cross_origin()
        def stream():
            data = json.loads(request.data)
            user_input = data["text"]
            top_k = data["top_k"]
            method = data["method"]

            def generate(model):
                if model.inuse:
                    return Response(json.dumps({
                        "method_name": "error",
                        "error": "Model in use"
                    }), status=409, mimetype='application/json')

                model.inuse = True
                with concurrent.futures.ThreadPoolExecutor() as executor:
                    future = executor.submit(model.run_pipeline, user_input, method, top_k)
                    while True:
                        if model.queue.empty():
                            if future.done():
                                break
                            time.sleep(0.01)
                            continue
                        else:
                            obj = model.queue.get()
                        if obj["method_name"] == "unknown": continue
                        if obj["method_name"] == "on_request_end":
                            yield json.dumps(obj)
                            break

                        try:
                            yield json.dumps(obj) + "\n"
                        except Exception as e:
                            model.inuse = False
                            print(e)

                    try:
                        future.result()
                    except Exception as e:
                        model.inuse = False
                        print(e)

                model.inuse = False

            return Response(stream_with_context(generate(self.model)))

        @self.app.route('/methods', methods=['GET'])
        @cross_origin()
        def methods():
            return Response(json.dumps({
                "methods": ["DFS_woFilter_w2"]
            }), status=200, mimetype='application/json')

    def run(self, host="0.0.0.0", port=5000, debug=True):
        self.app.run(use_reloader=False, host=host, debug=debug, port=port)
