from flask import Flask, request, render_template, redirect, url_for, make_response
from gevent.pywsgi import WSGIServer
from geventwebsocket.handler import WebSocketHandler
from multiprocessing import Process

class MalcomWeb(Process):
    def __init__(self, auth, listen_port, listen_interface, setup):
        super(MalcomWeb, self).__init__()
        self.setup = setup
        self.listen_port = setup['LISTEN_PORT']
        self.listen_interface = setup['LISTEN_INTERFACE']
        self.http_server = None
        self.app = Flask(__name__)
        self.app.secret_key = os.urandom(24)
        self.app.debug = True

    def run(self):
        self.start_server()

    def stop_server(self):
        pass

    def start_server(self):
        if not self.setup['AUTH']:
            self.app.config['LOGIN_DISABLED'] = True

        self.app.config['MODEL'] = ModelClass(self.setup)
        self.app.config['USER_MANAGER'] = UserManagerClass(self.setup)

        lm.init_app(self.app)
        lm.login_view = 'login'
        lm.session_protection = 'strong'
        lm.anonymous_user = self.app.config['USER_MANAGER'].get_default_user

        for key in self.setup:
            self.app.config[key] = self.setup[key]
        self.app.config['UPLOAD_DIR'] = ""

        self.app.config['MESSENGER'] = WebMessenger()

        @self.app.route('/')
        @login_required
        def index():
            return redirect(url_for('search'))

        @self.app.route('/search/', methods=['GET', 'POST'])
        @login_required
        def search(term=""):
            if request.method == 'POST':
                field = 'value'
                query = [{field: r.strip()} for r in request.form['bulk-text'].split('\r\n') if r.strip() != '']
                result_set = self.app.config['MODEL'].find({'$or': query})
            else:
                query = request.args.get('query', False)
                if query:
                    query = query.strip()
                field = request.args.get('field', 'value').strip()
                if not bool(request.args.get('strict', False)):
                    result_set = self.app.config['MODEL'].find({field: query})
                else:
                    result_set = self.app.config['MODEL'].find({field: re.compile(re.escape(query), re.IGNORECASE)})

            if query == False:
                return render_template('search.html', history=self.app.config['MODEL'].get_history())
            elif query == "":
                flash('Empty search query is empty.')
                return redirect(url_for('search'))

            base_elts = []
            base_ids = []
            evil_elts = {}

            for e in result_set:
                base_elts.append(e)
                base_ids.append(e['_id'])
                if 'evil' in e['tags']:
                    evil_elts[e['_id']] = e

            if len(base_elts) == 0 and request.method == 'GET':
                if not bool(request.args.get('log', False)):
                    flash('"{}" was not found. Use the checkbox above to add it to the database'.format(query))
                    return render_template('search.html', term=query, history=self.app.config['MODEL'].get_history())
                else:
                    new = self.app.config['MODEL'].add_text([query], tags=['search'])
                    if new:
                        flash('"{}" was not found. It was added to the database (ID: {})'.format(query, new['_id']))
                        self.app.config['MODEL'].add_to_history(query)
                    else:
                        flash('"{}" did not convert to a viable datatype'.format(query))
                    return render_template('search.html', term=query, history=self.app.config['MODEL'].get_history())

            if len(base_elts) == 0 and request.method == 'POST':
                flash('Your query did not yield any results. Use the checkbox above to add it to the database')
                return render_template('search.html', history=self.app.config['MODEL'].get_history())

            return find_related(field, query, base_elts, base_ids, evil_elts)

        self.http_server = WSGIServer((self.listen_interface, self.listen_port), self.app, handler_class=WebSocketHandler)
        self.http_server.serve_forever()
