from flask import Flask, render_template, request, redirect, session
from flask_login import UserMixin, LoginManager, login_user, logout_user, current_user
from flask_sqlalchemy import SQLAlchemy
from flask_wtf import FlaskForm
from wtforms import StringField, PasswordField
from wtforms.validators import ValidationError, DataRequired, EqualTo, Length
import os
import datetime

class WebApp:
    def __init__(self):
        self.app = Flask(__name__)
        self.app.config["SESSION_COOKIE_SECURE"] = False
        self.app.config["SQLALCHEMY_TRACK_MODIFICATIONS"] = False
        self.app.config["SQLALCHEMY_DATABASE_URI"] = "sqlite:///logontracer.db"
        self.app.config["SECRET_KEY"] = os.urandom(24)
        self.app.permanent_session_lifetime = datetime.timedelta(minutes=60)

        self.db = SQLAlchemy(self.app)
        self.login_manager = LoginManager()
        self.login_manager.init_app(self.app)

        class User(UserMixin, self.db.Model):
            id = self.db.Column(self.db.Integer, primary_key=True)
            username = self.db.Column(self.db.String(50), nullable=False, unique=True)
            urole = self.db.Column(self.db.String(20))

        class SettingForm(FlaskForm):
            password1 = PasswordField('Password', validators=[DataRequired(), EqualTo('password2', message='Passwords must match.'), Length(min=3, max=20)])
            password2 = PasswordField('Password (again)', validators=[DataRequired(), Length(min=3, max=20)])

        class LoginForm(FlaskForm):
            username = StringField('Username', validators=[DataRequired(), Length(min=3, max=50)])
            password = PasswordField('Password', validators=[DataRequired(), Length(min=3, max=20)])

        class RegistrationForm(FlaskForm):
            username = StringField('Username', validators=[DataRequired(), Length(min=3, max=50)])
            password1 = PasswordField('Password', validators=[DataRequired(), EqualTo('password2', message='Passwords must match.'), Length(min=3, max=20)])
            password2 = PasswordField('Password (again)', validators=[DataRequired(), Length(min=3, max=20)])

        class CaseForm(FlaskForm):
            case = StringField('Case', validators=[DataRequired()])

        with self.app.app_context():
            self.db.create_all()

        @self.login_manager.user_loader
        def load_user(user_id):
            return self.db.session.get(User, int(user_id))

        @self.login_manager.unauthorized_handler
        def unauthorized():
            return redirect('/login')

        @self.app.route('/login', methods=['GET', 'POST'])
        def login():
            if current_user.is_authenticated:
                return redirect('/')

            session.permanent = True
            form = LoginForm(request.form)
            if form.validate_on_submit():
                username = form.username.data
                password = form.password.data
                remember = True if request.form.get("remember") else False

                session["username"] = username
                session["password"] = password

                user = User.query.filter_by(username=username).first()
                login_user(user, remember=remember)
                return redirect('/')

            return render_template('login.html', form=form)

        @self.app.route('/signup', methods=['GET', 'POST'])
        @login_required(role="ADMIN")
        def signup():
            form = RegistrationForm(request.form)
            if form.validate_on_submit():
                username = form.username.data
                password = form.password1.data
                admin = True if request.form.get("admin") else False

                with self.app.app_context():
                    user = User(username=username, urole="ADMIN" if admin else "USER")
                    self.db.session.add(user)
                    self.db.session.commit()

                return redirect('/')
            return render_template('signup.html', form=form)

        @self.app.route('/setting', methods=['GET', 'POST'])
        @login_required(role="ANY")
        def setting():
            form = SettingForm(request.form)
            if form.validate_on_submit():
                username = current_user.username
                password = form.password1.data

                with self.app.app_context():
                    user_query = User.query.filter_by(username=username).first()
                    self.db.session.delete(user_query)
                    self.db.session.commit()

                    user = User(username=username, urole=user_query.urole)
                    self.db.session.add(user)
                    self.db.session.commit()

                session["password"] = password
                return redirect('/')
            return render_template('setting.html', form=form)

        @self.app.route('/logout')
        @login_required(role="ANY")
        def logout():
            logout_user()
            return redirect('/login')

        @self.app.route('/')
        @login_required(role="ANY")
        def index():
            return render_template("index.html")

    def run(self, host='0.0.0.0', port=8080):
        self.app.run(host=host, port=port)

    def login_required(self, role="ANY"):
        def wrapper(fn):
            @wraps(fn)
            def decorated_view(*args, **kwargs):
                if not current_user.is_authenticated:
                    return self.login_manager.unauthorized()
                urole = current_user.get_urole()
                if ((urole != role) and (role != "ANY")):
                    return self.login_manager.unauthorized()
                return fn(*args, **kwargs)
            return decorated_view
        return wrapper
